	tit_clearActionInfo();																	
	tit_addSearchActionInfo("cs42:CS4202001A_I3");									
	tit_callService(		
			""
			, ""							
			, "ds_cond=ds_cond"							
			, ""							
			, ""							
			, ""							
			, true							
			, false							
			, true); 
}

function grd_list2_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 0) {
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		gfn_sortGrid(obj,nCell);
	}
}

function btn_rowcnt_OnClick(obj)
{
	var rowi = parseInt(ed_rowcnt.Text);

	for(var i=0; i < rowi; i++) {
		ds_list.SetColumn(i, "CHK",  true); 
	}
}

