		, "ds_bsu=ds_bsu"
		, ""
		, ""						
		, true							
		, false							
		, true); 
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "S004") {

		if(varOldValue != varNewValue) {
			var findValue = substr(ds_list.GetColumn(nRow,"S004"),2,1) + "2" + ds_list.GetColumn(nRow,"SEAL");
			var findIdx = ds_zcst703.FindRow("S007", findValue);
			ds_list.SetColumn(nRow,"S001",ds_zcst703.GetColumn(findIdx,"S001"));	//외부기관구분코드
			ds_list.SetColumn(nRow,"S002",ds_zcst703.GetColumn(findIdx,"S002"));	//국기호 5자리
			ds_list.SetColumn(nRow,"S003",ds_zcst703.GetColumn(findIdx,"S003"));	//접수코드
			ds_list.SetColumn(nRow,"S005",ds_zcst703.GetColumn(findIdx,"S005"));	//결재방법
			ds_list.SetColumn(nRow,"S006",ds_zcst703.GetColumn(findIdx,"S006"));	//내용문 유형
			ds_list.SetColumn(nRow,"S007",ds_zcst703.GetColumn(findIdx,"S007"));	//내용문 상세 종류
			ds_list.SetColumn(nRow,"S008",ds_zcst703.GetColumn(findIdx,"S008"));	//내용문 종류
			ds_list.SetColumn(nRow,"S009",ds_zcst703.GetColumn(findIdx,"S009"));	//내용문 매수
			ds_list.SetColumn(nRow,"S011",ds_zcst703.GetColumn(findIdx,"S011"));	//봉투 종류
			ds_list.SetColumn(nRow,"S012",ds_zcst703.GetColumn(findIdx,"S012"));	//칼라우편물/흑백우편물
			ds_list.SetColumn(nRow,"S013",ds_zcst703.GetColumn(findIdx,"S013"));	//메일머지 플래그
			ds_list.SetColumn(nRow,"S014",ds_zcst703.GetColumn(findIdx,"S014"));	//이면구분
			ds_list.SetColumn(nRow,"S015",ds_zcst703.GetColumn(findIdx,"S015"));	//동봉물 개수
			ds_list.SetColumn(nRow,"S016",ds_zcst703.GetColumn(findIdx,"S016"));	//반송불필요여우
			ds_list.SetColumn(nRow,"S017",ds_zcst703.GetColumn(findIdx,"S017"));	//승인번호 10자리
			ds_list.SetColumn(nRow,"S023",ds_zcst703.GetColumn(findIdx,"S023"));	//발송인 명
			ds_list.SetColumn(nRow,"S024",ds_zcst703.GetColumn(findIdx,"S024"));	//발송인 우편번호
			ds_list.SetColumn(nRow,"S025",ds_zcst703.GetColumn(findIdx,"S025"));	//발송인 기본주소
			ds_list.SetColumn(nRow,"S026",ds_zcst703.GetColumn(findIdx,"S026"));	//발송인 상세주소
			ds_list.SetColumn(nRow,"S045",ds_zcst703.GetColumn(findIdx,"S045"));	//첨부파일 여부
		}
	}
}
