#include "LIB::commons.js";	// 공통 스크립트 
#include "LIB::common_cs.js";	// 서비스 공통 스크립트
//=========================================================================================
// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다. 
//=========================================================================================
var flag_chk = false;
var vFullSrch_auth = false;	// 조회그룹권한(전체)
var vGwangjuSrch_auth = false;	// 조회그룹권한(광주)
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);	
	cal_dat1.Value = substr(gfn_getCurrDate(),0,6) + "01";
	cal_dat2.Value = gfn_getLeapLastDay(gfn_getCurrDate()); // 당월 마지막 일자 가져오기	

	//=================== 전체조회 가능 권한
	//G_USER_ID in ("2021200","2023466","2019523","2017938","2017750","2023687","2018900","2029758","2999999")
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS4204001B");
	ds_rcond.SetColumn(0, "GBN", "01");	
	vFullSrch_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();
	//=================== 광주 1팀 2팀 강화영
	//G_USER_ID in ("2019752")
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS4204001B");
	ds_rcond.SetColumn(0, "GBN", "02");	
	vGwangjuSrch_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();
	//=======================================
	//지사 콤보 리스트 가져오기
	var codeArr = ["VKGRP"];
    var dsObjArr = [ds_vkgrp_cd];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	// 서비스 본부 팀코드 가져오기
	ds_vkgrp_cd.Filter("(CODE ='') OR (CODE >= 'EA1' AND CODE <= 'EFP')");
//	trace(G_USER_NAME + G_SAP_USER_VGCD);
	cb_vkgrp.Index = ds_vkgrp_cd.FindRow("CODE", G_SAP_USER_VGCD);
	//============================ 조회 엽업그룹 지정
	//// 전체 조회 가능 
	// if(G_USER_ID == "2021200" || G_USER_ID == "2023466" || G_USER_ID == "2019523" // 이하림 이경해
	// || G_USER_ID == "2017938" // 최인식
	// || G_USER_ID == "2017750" || G_USER_ID == "2023687" || G_USER_ID == "2018900" || G_USER_ID == "2029758" || G_USER_ID == "2999999") { // 이주형 용아름
		// ds_vkgrp_cd.Filter("(CODE ='') OR (CODE >= 'EA1' AND CODE <= 'EFP')");
		// cb_vkgrp.Index = 0;
		// cb_vkgrp.Enable = true;
	// }
	// if(G_USER_ID == "2019752"){ // 광주 1팀 2팀 강화영 
		// ds_vkgrp_cd.Filter("(CODE ='EFC') OR (CODE ='EFP')");
		// cb_vkgrp.Enable = true;
	// }
	if (vFullSrch_auth == true)
	{
		ds_vkgrp_cd.Filter("(CODE ='') OR (CODE >= 'EA1' AND CODE <= 'EFP')");
		cb_vkgrp.Index = 0;
		cb_vkgrp.Enable = true;
	}
	if (vGwangjuSrch_auth == true)
	{
		ds_vkgrp_cd.Filter("(CODE ='EFC') OR (CODE ='EFP')");
		cb_vkgrp.Enable = true;
	}
	//=======================================
	cb_trans.index = 0;
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	ds_list.ClearData();
	var user_grp = gfn_getUserGroup();

	if(gfn_isNull(cal_dat1, true, st_dat.value)) {
		return;
	}
	if(gfn_isNull(cal_dat2, true, st_dat.value)) {
		return;
	}

	if(cb_trans.Value == "A") {
		flag_chk = true;
	} else {
		flag_chk = false;
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs42:CS4204001B_S1");

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list=ds_list"
		, ""
		, "fn_afterQuery"
		, true
		, ""
		, true
	);
}
/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

