{
	gfn_sortGrid(obj,nCell);
}

function grd_list2_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 0 and flag_chk == true) {
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		gfn_sortGrid(obj,nCell);
	}
}
