function btn_pjt_OnClick(obj)
{
	var arg = "pjt="+quote(ed_pjt.Text);
	gfn_openPopup("CS9601002A",true,arg);
}

function grd_list2_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var arg = "pjt="+quote(ds_list.GetColumn(nRow, "PJT"))
	        + " docno="+quote(ds_list.GetColumn(nRow, "DOCNO"))
	        + " p_gbn=A";

	gfn_openPopup("CS4204001A",true,arg);
}

function btn_trans_OnClick(obj)
{
	// if(G_USER_ID != "2021200"){
		// alert("전자우편 신청 불가능합니다. 서비스 관리부에 문의 바랍니다.");
		// return;
	// }

	if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showAlert("CW00013");		//처리할 자료를 먼저 선택해 주십시오.
		return;
	}	

	ds_list_copy.ClearData();
	var j = 0;
	var fcnt = 0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) {
			// 전자우편 필수 항목 체크
			if(length(trim(ds_list.GetColumn(i, "S004"))) == 0 ){
				gfn_showAlert("문서번호 : "+ds_list.GetColumn(i, "DOCNO") +" 의 발송구분 정보가 누락되었습니다.");
				return false;	
			}
			if(length(trim(ds_list.GetColumn(i, "RECEV_NM"))) == 0 ){
				gfn_showAlert("문서번호 : "+ds_list.GetColumn(i, "DOCNO") +" 의 수신인명 정보가 누락되었습니다.");
				return false;	
			}
			var zip = length(trim(ds_list.GetColumn(i, "ZIP")));
			var adr1 = length(trim(ds_list.GetColumn(i, "ADR1")));
			var adr2 = length(trim(ds_list.GetColumn(i, "ADR2")));
			var dzip = length(trim(ds_list.GetColumn(i, "DZIP")));
			var dadr1 = length(trim(ds_list.GetColumn(i, "DADR1")));
			var dadr2 = length(trim(ds_list.GetColumn(i, "DADR2")));
			var nzib = length(trim(ds_list.GetColumn(i, "NZIB")));

			var post_chk  = ds_list.GetColumn(i, "DZIP");
			var post_chk2 = ds_list.GetColumn(i, "NZIB");
			post_chk  = replace(post_chk , " ", "");
			post_chk  = replace(post_chk , "_", "");
			post_chk  = replace(post_chk , "-", "");
			post_chk  = replace(post_chk , "000000", "");
			post_chk2 = replace(post_chk2 , " ", "");
			post_chk2 = replace(post_chk2 , "_", "");
			post_chk2 = replace(post_chk2 , "-", "");
			post_chk2 = replace(post_chk2 , "00000", "");

			if(length(post_chk2) <> 5) {
				if(length(post_chk) < 6) {
					gfn_showAlert("문서번호 : "+ds_list.GetColumn(i, "DOCNO") +" 의 신규 우편번호를 확인하십시오.");
					return false;	
				}
			}

			if( dzip == 0 or dadr1 == 0 or dadr2 == 0 ){
				gfn_showAlert("문서번호 : "+ds_list.GetColumn(i, "DOCNO") +" 의 수신처 주소 정보가 누락되었습니다.");
				return false;	
			}
			// 공문 내용 필수 항목 체크
			var usr = length(trim(ds_list.GetColumn(i, "USR")));
			var bmt = length(trim(ds_list.GetColumn(i, "BMT")));
			var pho = length(trim(ds_list.GetColumn(i, "PHO")));
			var nam = length(trim(ds_list.GetColumn(i, "NAM")));
			var adr = length(trim(ds_list.GetColumn(i, "ADR")));
			var txt = length(trim(ds_list.GetColumn(i, "TXT")));
			var rqt = length(trim(ds_list.GetColumn(i, "RQT")));
			var ref = length(trim(ds_list.GetColumn(i, "REF")));

			if( usr == 0 or bmt == 0 or pho == 0 or nam  == 0 or adr == 0 or txt == 0 or rqt == 0 or ref ==0){
				gfn_showAlert("문서번호 : "+ds_list.GetColumn(i, "DOCNO") +" 의 공문필수항목 정보가 누락되었습니다.");
				return false;	
			}

			ds_list_copy.AddRow();
			ds_list_copy.SetColumn(j ,"PJT"  , ds_list.GetColumn(i,"PJT"));
			ds_list_copy.SetColumn(j ,"TEM"  , ds_list.GetColumn(i,"TEM"));
			ds_list_copy.SetColumn(j ,"YDT"  , ds_list.GetColumn(i,"YDT"));
			ds_list_copy.SetColumn(j ,"MDT"  , ds_list.GetColumn(i,"MDT"));
			ds_list_copy.SetColumn(j ,"SEQ"  , ds_list.GetColumn(i,"SEQ"));
			ds_list_copy.SetColumn(j ,"S004" , ds_list.GetColumn(i,"S004"));
			ds_list_copy.SetColumn(j ,"SEAL" , "F");
			j++;
		}	
	}	

	if(!gfn_showConfirm("전자우편 서비스 신청을 하시겠습니까?          " + "총 " +  j + " 건")) return;


	tit_callService(
        "CS4201001A_ACT"
        , "insertHeader"
        , "ds_list=ds_list_copy"
        , "ds_list=ds_list ds_error=ds_error"
        , ""
        , "fn_afterInsert"
        ,""
        ,true
        ,"");	
}

function fn_afterInsert(errCode, errMsg) {

	if(errCode != 0) {
		gfn_showAlert("CE00001");
	} else {
		gfn_showAlert("전자우편 서비스 신청이 완료되었습니다.");
		fn_query();
	}
}
