// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	var firstDay = substr(gfn_getCurrDate(),0,6) + "01";
	cal_rec_fm.value = firstDay;
	cal_rec_to.value = gfn_getCurrDate();

	//접수유형 콤보 SELECT
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs50:CS5001001A_S1");
	tit_callService(
		"PDA_ACT"
		, ""
		, "ds_cond=ds_cond"
		, "ds_gubun_cd=ds_list"
		, ""
		, "fn_afterQueryGubun"
		, true
		, true);

	//고장원인 콤보 SELECT
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs50:CS5001001A_S2");
	tit_callService(
		"PDA_ACT"
		, ""
		, "ds_cond=ds_cond"
		, "ds_reason_cd=ds_list"
		, ""
		, "fn_afterQueryReason"
		, true
		, true);
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
