function fn_completeInit(sCode, sDsObj, sCodeType) {

}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 조회 (header 조회)
********************************************************************************/
function fn_query() {
	var user_grp = gfn_getUserGroup();
	var t_dat_fm = cal_rec_fm.value;
	var t_dat_to = cal_rec_to.value;
	var t_term = gfn_getDayCnt(ds_days, t_dat_fm, t_dat_to);

/*
	if((length(t_dat_fm) > 0 && length(t_dat_to) == 0) || (length(t_dat_fm) == 0 && length(t_dat_to) > 0)) {
		gfn_showAlert("접수일자를 확인 하시기 바랍니다.");
		return false;
	}
*/

	if ( !gfn_checkFromToObj(cal_rec_fm, cal_rec_to, true, Static1.value, 0, true) ) 
    {
		return;
	}


	if(length(ed_upn.value) == 0 && length(cb_gubun.value) == 0 && length(cb_reason.value) == 0 
	   && length(div_code.cb_mid.value) == 0 && length(div_code.cb_small.value) == 0) {
		//if(t_term > 31) {
		//	gfn_showAlert("접수일자를 한달이내로 하시기 바랍니다.");
		if(t_term > 9) {
			gfn_showAlert("접수일자를 10일 이내로 하시기 바랍니다.");
			return false;
		}
	}

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		/*
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
		*/
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
			//ds_cond.SetColumn(0, "VKGRP", '');
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
			//ds_cond.SetColumn(0, "VKGRP", '');
		} else if(div_code.cb_big.value == '3') { // 협력사 콤보1이 팀 
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
			//ds_cond.SetColumn(0, "VKGRP", '');
		} else { // 협력사 콤보1이 부서일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_small.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "DPT", substr(trim(div_code.cb_mid.value),0,2));
			ds_cond.SetColumn(0, "BSU", '');
			//ds_cond.SetColumn(0, "VKGRP", trim(div_code.cb_small.value));
		}		

	}


    //==============문제점 수정  관련 추가====================
	ds_cond2.ClearData();
	ds_cond2.copy(ds_cond);
	if(length(ed_upn.value) > 0) {
		ds_cond2.SetColumn(0, "UPN", ed_upn.value);
		ds_cond2.SetColumn(0, "CST", ed_cst.value);
		ds_cond2.SetColumn(0, "REC_FMD", "");
		ds_cond2.SetColumn(0, "REC_TOD", "");
		ds_cond2.SetColumn(0, "GBN", cb_gubun.value);  //접수유형
		ds_cond2.SetColumn(0, "RSN", cb_reason.value); //고장원인

		ds_cond.SetColumn(0, "TEM", "");
		ds_cond.SetColumn(0, "BPM", "");
		ds_cond.SetColumn(0, "ARA", "");
		ds_cond.SetColumn(0, "DPT", "");
		ds_cond.SetColumn(0, "BSU", "");


	} else {
		ds_cond2.SetColumn(0, "UPN", "");
		ds_cond2.SetColumn(0, "CST", "");
		//ds_cond2.SetColumn(0, "REC_FMD", cal_rec_fm.value);
		//ds_cond2.SetColumn(0, "REC_TOD", cal_rec_to.value);
		//ds_cond2.SetColumn(0, "GBN", cb_gubun.value);  //접수유형
		//ds_cond2.SetColumn(0, "RSN", cb_reason.value); //고장원인
	}

    //========================================================

/*
	if(length(ed_upn.value) > 0) {
		ds_cond.SetColumn(0, "UPN", ed_upn.value);
		ds_cond.SetColumn(0, "CST", ed_cst.value);
		ds_cond.SetColumn(0, "REC_FMD", "");
		ds_cond.SetColumn(0, "REC_TOD", "");
		ds_cond.SetColumn(0, "GBN", cb_gubun.value);  //접수유형
		ds_cond.SetColumn(0, "RSN", cb_reason.value); //고장원인
	} else {
		ds_cond.SetColumn(0, "UPN", "");
		ds_cond.SetColumn(0, "CST", "");
		ds_cond.SetColumn(0, "REC_FMD", cal_rec_fm.value);
		ds_cond.SetColumn(0, "REC_TOD", cal_rec_to.value);
		ds_cond.SetColumn(0, "GBN", cb_gubun.value);  //접수유형
		ds_cond.SetColumn(0, "RSN", cb_reason.value); //고장원인
	}
*/

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs50:CS5001001A_S3");
	tit_callService(
        "PDA_ACT"
        , ""
        , "ds_cond=ds_cond2"  //ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showMsg("CI00002", ds_list.RowCount()+"");
}

/********************************************************************************
* 기      능   : 조회 Callback  : 접수유형 콤보
********************************************************************************/
function fn_afterQueryGubun(errCode, errMsg) {
	ds_gubun_cd.InsertRow(0);
	ds_gubun_cd.SetColumn(0, "CODE", "");
	ds_gubun_cd.SetColumn(0, "CODE_NAME", "- 전체 -");
	cb_gubun.Index = 0;
}

/********************************************************************************
* 기      능   : 조회 Callback  : 고장원인 콤보
********************************************************************************/
function fn_afterQueryReason(errCode, errMsg) {
	ds_reason_cd.InsertRow(0);
	ds_reason_cd.SetColumn(0, "CODE", "");
	ds_reason_cd.SetColumn(0, "CODE_NAME", "- 전체 -");
	cb_reason.Index = 0;
}

