/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	if(ufn_isNull(cal_from, true, "시작일", cal_from)) return false;			//최소입력조건:시작일
	if(ufn_isNull(cal_to, true, "종료일", cal_to)) return false;				//최소입력조건:종료일

	ds_cond.SetColumn(0, "LAND1", trim(div_code.cb_mid.value));	
	ds_cond.SetColumn(0, "LIFNR", trim(div_code.cb_small.value));	

//	DEBUG("fn_query", "ds_cond is " + ds_cond.SaveXml());

	ufn_clearGrid();
	gfn_clearMsgBar();

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs80:CS8001001A_S1", true, true);					//Master List
	tit_addSearchActionInfo("cs80:CS8001001A_S2", false, true);					//Master List에 대한 Detail List
	tit_addSearchActionInfo("cs80:CS8001001A_S3", false, true);					//LT1, LT2, 성공율 구하기

	fn_createWait("",this);														//"처리중입니다. 잠시 기다려주십시오." 윈도우 create

	grd_list_detail.visible = false;

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list_master=ds_list_master ds_list_detail=ds_list_detail ds_add_info=ds_add_info"
        , ""
        , "fn_afterQueryMaster"
        , true);
}

