var pjt, ara;
var pageGubun ; //  경로 구분  

function fn_OnLoadCompleted(obj) {
	gfn_initForm();


	var codeArr = ["CS112","EL_AUSE","BS403"];
	var dsObjArr = [ds_zgubun,ds_spec4,ds_bld];
	var addTypeArr = [G_CODE_BLANK,G_CODE_BLANK,G_CODE_NONE];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	ds_cond.SetColumn(0, "PJT", pjt);
}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {

	if(pageGubun == "EMAKE") {
		fn_set_Emake();
	} else if (pageGubun == "MMAKE") {
		fn_set_Mmake();
	} else if (pageGubun == "MODIFY") {
		grd_list.Editable = false ;
		fn_query();
	} 
}
/********************************************************************************
* 기      능   : form UnloadCompleted
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

