	ds_list.SetColumn(0, "MAC", "Y");
	ds_list.SetColumn(0, "RSN_NM", "보수자재 청구");


/**/

	ds_list.SetColumn(0, "HNO", "L01");
	ds_list.SetColumn(0, "SPEC1", "SSVF");
	ds_list.SetColumn(0, "SPEC2", "PF630-2SSO60-4");
	ds_list.SetColumn(0, "SPEC4", "PF");
	ds_list.SetColumn(0, "SPEC6", "630");
	ds_list.SetColumn(0, "SPEC3", "60");
