********************************************************************************/
function fn_query() {

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs80:CS8002002A_S1");
	tit_addSearchActionInfo("cs80:CS8002002A_S2");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_head=ds_head ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 호기생성
********************************************************************************/
function fn_save()
{	
	if(ds_list.rowcount() == 0) {
		gfn_showAlert("저장할 데이타가 없습니다.");
		return false;
	}

	if(pageGubun == "EMAKE") {
		fn_save_Emake();
	} else if (pageGubun == "MMAKE") {
		fn_save_Mmake();
	} 
}

function fn_save_Emake(){

	for(var i=0; i<ds_list.RowCount(); i++) {	
		ds_list.SetColumn(i, "CHECK", true );
	}

	ds_list2.ClearData();
	var j = 0 ;
	for(var i=0; i<ds_list.RowCount(); i++) {					
		if(ds_list.GetColumn(i, "CHECK") == true) { //체크박스가 true 일 경우				

			if(fn_saveCheck(i)==true){

				ds_list2.AddRow();
				ds_list2.SetColumn(j, "PJT", ds_list.GetColumn(i, "PJT"));
				ds_list2.SetColumn(j, "HNO", ds_list.GetColumn(i, "HNO"));
				ds_list2.SetColumn(j, "SPEC1", ds_list.GetColumn(i, "SPEC1"));
				ds_list2.SetColumn(j, "SPEC2", ds_list.GetColumn(i, "SPEC2"));
				ds_list2.SetColumn(j, "SPEC3", ds_list.GetColumn(i, "SPEC3"));
				ds_list2.SetColumn(j, "SPEC4", ds_list.GetColumn(i, "SPEC4"));
				ds_list2.SetColumn(j, "SPEC6", ds_list.GetColumn(i, "SPEC6"));
				ds_list2.SetColumn(j, "SPEC7", ds_list.GetColumn(i, "SPEC7"));
				ds_list2.SetColumn(j, "BLD", ds_list.GetColumn(i, "BLD"));				

				ds_list2.SetColumn(j, "MAC", ds_list.GetColumn(i, "MAC"));
				ds_list2.SetColumn(j, "EXC", ds_list.GetColumn(i, "EXC"));
				j++;
			} else {
				return false;
			}
		}
	}


	//trace(ds_head.SaveXML());
	//trace(ds_list2.SaveXML());
	//return;
	tit_clearActionInfo();	
	tit_addSingleActionInfo("cs80:CS8002002A_I3"); // 121G
	tit_addMultiActionInfo("cs80:CS8002002A_I4");	// 122G
	tit_addMultiActionInfo("cs80:CS8002002A_I5"); // 111G
	tit_addMultiActionInfo("cs80:CS8002002A_I6"); // 202 
	tit_callService(
	""
	, ""
	, "ds_head=ds_head ds_list2=ds_list2"
	, ""
	, ""
	, "fn_afterSave"
	, true
	, true
	, true
	);      	

}


function fn_save_Mmake(){

	btn_search_kunnr_OnClick();
	for(var i=0; i<ds_list.RowCount(); i++) {	
		ds_list.SetColumn(i, "CHECK", true );
	}

	ds_list2.ClearData();

	var j = 0;
	for(var i=0; i<ds_list.RowCount(); i++){				
		if(ds_list.GetColumn(i, "CHECK") == true) {//체크박스가 true 일 경우	


			if(fn_saveCheck(i)==true){

				ds_list2.AddRow();
				ds_list2.SetColumn(j, "HNO", ds_list.GetColumn(i, "HNO"));
				ds_list2.SetColumn(j, "SPEC1", ds_list.GetColumn(i, "SPEC1"));
				ds_list2.SetColumn(j, "SPEC2", ds_list.GetColumn(i, "SPEC2"));
				ds_list2.SetColumn(j, "SPEC3", ds_list.GetColumn(i, "SPEC3"));
				ds_list2.SetColumn(j, "SPEC4", ds_list.GetColumn(i, "SPEC4"));
				ds_list2.SetColumn(j, "SPEC6", ds_list.GetColumn(i, "SPEC6"));
				ds_list2.SetColumn(j, "SPEC7", ds_list.GetColumn(i, "SPEC7"));
				ds_list2.SetColumn(j, "BLD", ds_list.GetColumn(i, "BLD"));				

				ds_list2.SetColumn(j, "MAC", "Y");
				ds_list2.SetColumn(j, "EXC", "");

				j++;
			}else {
				return false;
			}
		}
	}

	ds_pjt_0.ClearData(); 
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs80:CS8002001A_S3");

	tit_callService(
		""
		, ""
		, ""
		, "ds_pjt_0=ds_pjt"
		, ""
		, ""
		, true
		, true
		, true
		);


	// M 채번
	if(ds_pjt_0.RowCount() > 0 ) {
		fn_save_Mmake2() ;
	} else {
		ALERT("M 프로젝트 생성 실패 ");
	}
}
function fn_save_Mmake2() {

	ds_cond.SetColumn(0, "PJT", ds_pjt_0.GetColumn(0, "MJT")) ;
	ds_head.SetColumn(0, "PJT", ds_pjt_0.GetColumn(0, "MJT")) ;

	for(var i=0; i<ds_list2.RowCount(); i++){	
		ds_list2.SetColumn(0, "PJT", ds_pjt_0.GetColumn(0, "MJT")) ;
	}
	//trace(ds_head.SaveXML());
	//trace(ds_list2.SaveXML());

	tit_clearActionInfo();	
	tit_addSingleActionInfo("cs80:CS8002002A_I1");	//zmaster01
	tit_addMultiActionInfo("cs80:CS8002002A_I2");  // zmaster02
	tit_addSingleActionInfo("cs80:CS8002002A_I3"); // 121G
	tit_addMultiActionInfo("cs80:CS8002002A_I4");	// 122G
	tit_addMultiActionInfo("cs80:CS8002002A_I5"); // 111G
	tit_addMultiActionInfo("cs80:CS8002002A_I6"); // 202 
	tit_callService(
	""
	, ""
	, "ds_head=ds_head ds_list2=ds_list2"
	, ""
	, ""
	, "fn_afterSave"
	, true
	, true
	, true
	);      

}

function fn_saveCheck(i){

	var seq = i + 1 + "번째 호기의 ";

	if(length(trim(ds_list.GetColumn(i, "HNO"))) == 0){		
		var arr = [seq + trim(st_hno.Text)];
		gfn_showAlert("CW00001", arr);
		return false;
	}

	if(length(trim(ds_list.GetColumn(i, "SPEC4"))) == 0){		
		var arr = [seq + trim(st_spec4.Text)];
		gfn_showAlert("CW00001", arr);
		return false;
	}	

	if(length(trim(ds_list.GetColumn(i, "SPEC1"))) == 0){		
		var arr = [seq + trim(st_spec1.Text)];
		gfn_showAlert("CW00001", arr);
		return false;
	}
	if(length(trim(ds_list.GetColumn(i, "SPEC2"))) == 0){		
		var arr = [seq + trim(st_spec2.Text)];
		gfn_showAlert("CW00001", arr);
		return false;
	}
	if(length(trim(ds_list.GetColumn(i, "SPEC3"))) == 0){		
		var arr = [seq + trim(st_spec3.Text)];
		gfn_showAlert("CW00001", arr);
		return false;
	}
	if(length(trim(ds_list.GetColumn(i, "SPEC6"))) == 0){		
		var arr = [seq + trim(st_spec6.Text)];
		gfn_showAlert("CW00001", arr);
		return false;
	}
	if(length(trim(ds_list.GetColumn(i, "SPEC7"))) == 0){		
		var arr = [seq + trim(st_spec7.Text)];
		gfn_showAlert("CW00001", arr);
		return false;
	}
	if(length(trim(ds_head.GetColumn(0, "KUNNR"))) == 0){		
		var arr = [trim(st_kunnr.Text)];
		gfn_showAlert("CW00001", arr);
		return false;
	}
	if(length(trim(ds_head.GetColumn(0, "SPT"))) == 0){		
		var arr = [trim(st_spt.Text)];
		gfn_showAlert("CW00001", arr);
		return false;
	}
	if(length(trim(ds_head.GetColumn(0, "ADDR1"))) == 0){		
		var arr = [trim(st_addr.Text)];
		gfn_showAlert("CW00001", arr);
		return false;
	}
	return true;
}
