/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	gfn_changeFlag( obj, strColumnID, nRow, "FLAG" );

	var str = substr(varNewValue, 0, 1);
	if(strColumnID == "HNO" && varNewValue != ""){
		if(!(str=="L" || str=="S" || str=="W" || str=="J" || str=="G")){
			gfn_showMsg("CW00112");
			ds_list.FireEvent=false;
			ds_list.SetColumn(0, "HNO", "");
			ds_list.FireEvent=true;
			ed_hno.SetFocus();
		} else {
			gfn_clearMsgBar();
		}
		if(length(trim(varNewValue)) < 3){
			gfn_showMsg("CW00237");
			ds_list.FireEvent=false;
			ds_list.SetColumn(0, "HNO", "");
			ds_list.FireEvent=true;
			ed_hno.SetFocus();
		} else {
			gfn_clearMsgBar();
		}
	}	
}

function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ){
		// 그리드 표시할 컬럼 선택 
		//gfn_selectGridCol(obj);
		for(var i=0; i<ds_list.RowCount(); i++) {
			if(ds_list.GetColumn(i, "PST") != "A6"){		
				ds_list.SetColumn(i, "CHECK", true);
			}
		}	
	} else{
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}
function ufn_openPjt(obj)
{
	var arg = "pjt="+quote(ed_pjt.Text)+" strId=M";

	var rtn = gfn_openPopup("CS9601002A",true,arg);
	fn_query();
}


/********************************************************************************
* 기      능   : 고객번호 입력 후 정보 조회 
********************************************************************************/
function btn_search_kunnr_OnClick(obj)
{
	if (length(ed_kunnr.Text) > 0) {

		ds_kunnr_info.ClearData();

		tit_clearActionInfo();
		tit_addSearchActionInfo("cs80:CS8002002A_S3");
		tit_callService(
			""
			, ""
			, "ds_head=ds_head"
			, "ds_kunnr_info=ds_kunnr_info"
			, ""
			, "fn_set_kunnr_info"
			, true
			, false
			, true);

	} else {

		ed_land1.Text = "";
		ed_nam.Text   = "";
		ed_kunnr.Text = "";
	}
}
function fn_set_kunnr_info() {

	ed_land1.Text = ds_kunnr_info.GetColumn(0, "LAND1_NM");
	ed_nam.Text   = ds_kunnr_info.GetColumn(0, "NAME1");
	ed_kunnr.Text   = ds_kunnr_info.GetColumn(0, "KUNNR");
}
