/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	if(!gfn_checkDate(cal_reqdate_fm.value, cal_reqdate_to.value, true, cal_reqdate_fm)) {
		return;
	}

	if(length(trim(ed_upn.Text)) > 0 && length(trim(ed_upn.Text)) < 6) {
		gfn_showAlert("통합Proj는 최소 6자리를 입력하셔야 합니다.");
		return;
	}


	ds_cond.SetColumn(0, "LAND1", trim(div_code.cb_mid.value));	
	ds_cond.SetColumn(0, "LIFNR", trim(div_code.cb_small.value));	

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	ds_list.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs80:CS8003001A_S1");
	tit_addSearchActionInfo("cs80:CS8003001A_S2");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_mm031=ds_mm031 ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}


