}
/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {
	if (ds_mm031.rowcount() < 1) return;

	// Excel Export 
	//==========================================================================================
	/*2019.10.11  7003043 김종남 변경
	  exportExcelEx로 export 할때 엑셀파일의 내용이 깨지는 경우(메모리때문으로 생각됨)가 있어
	  saveExcel 로 변경함

	 // gfn_exportExcel(grd_sum, true ); -> gfn_saveToExcel(grd_sum, true );
	// 하부 그리드 출력
	if (gfn_getUserGroup() > "30")
	{
		ds_list.UnFilter();
		grd_list.ExportExcel("grd_list",false,false);
		//grd_list.ExportExcelEx("grd_list","A1",false,true,true);
		ds_list.filter("REQNO == " + quote( ds_mm031.GetColumn(ds_mm031.currow,"REQNO")));
	}
	*/
	//=========================================================================================

	gfn_saveToExcel(grd_sum, , , , , true );
	// 하부 그리드 출력
	if (gfn_getUserGroup() > "30")
	{
		ds_list.UnFilter();
		//grd_list.ExportExcel("grd_list",false,false);
		gfn_saveToExcel(grd_list, , , , , true);
		ds_list.filter("REQNO == " + quote( ds_mm031.GetColumn(ds_mm031.currow,"REQNO")));
	}
}

/********************************************************************************
* 기      능   : 승인 10
********************************************************************************/
function fn_approval() {

	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_mm031, ds_mm031_0, "CHECK=1");

	for( var i=0; i < ds_mm031_0.rowcount; i++ )
	{
		for( var j=0; j < ds_list.rowcount; j++ )
		{
			if(ds_mm031_0.GetColumn(i,"REQNO") == ds_list.GetColumn(j,"REQNO")) 
			{
				ds_list.SetColumn(j,"CHECK","1");
			} else {
				ds_list.SetColumn(j,"CHECK","");
			}
		}
	}

	// 처리 데이터 확인
	if (ds_mm031_0.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}

	var user_grp = gfn_getUserGroup();

	// 청구 승인자 권한 확인
	// 해외부품 자재청구 번호별 확인 화면에서 부품 승인을 위해서는 ZL_CODE(공통코드테이블)에 해당자의 사번을 등록하여야 함.
	// 등록예시) 사번이 2025884인 경우
	// MANDT : 183 , LANG : KO, CODE : AP_GUBN, CODE2 : 2025884, CTEXT1 : 청구 승인 구분, CTEXT2 : 3, DTEXT1 : 20180723, DTEXT2 : 2018209
	// SAP 운영코드, 언어     , 자재청구코드  , 해당자의 사번  , 자재청구코드명         , 확인안됨  , 등록일자         , 등록자의 사번
	var arr = split(G_ORG_USER_INFO, "^", true);
	var apgubn = ds_apgubn.GetColumn(ds_apgubn.searchRow("CODE== " + quote(arr[0])),"CODE_NAME");

	// 청구 승인자 권한 확인
	if (user_grp < 30)
	{
		gfn_showAlert("CW00071", "승인");
		return;
	}

	// 청구접수 대상 자료 확인
	var able = false;
	for (var i = 0; i < ds_mm031_0.rowcount ; i++)
	{
		able = true;
		// TRACE(" RECE_F : " +ds_mm031_0.GetColumn(i, "RECE_F"));
		// TRACE(" REQKIND : " +ds_mm031_0.GetColumn(i, "REQKIND"));
		// TRACE(" MMPR_DEL : " +ds_mm031_0.GetColumn(i, "MMPR_DEL"));
		// TRACE(" apgubn : " +apgubn);
		// TRACE(" POSID : " +length(ds_mm031_0.GetColumn(i, "POSID")));
		if(
			(ds_mm031_0.GetColumn(i, "RECE_F") != "2" && ds_mm031_0.GetColumn(i, "RECE_F") != "6") 
			|| (
					(( ds_mm031_0.GetColumn(i, "REQKIND") == "1" 
					|| (ds_mm031_0.GetColumn(i, "REQKIND") == "6" && length(ds_mm031_0.GetColumn(i, "POSID")) < 1) //WBS번호 추가
					|| (ds_mm031_0.GetColumn(i, "REQKIND") == "G" && length(ds_mm031_0.GetColumn(i, "POSID")) < 1) //WBS번호 추가
					|| ds_mm031_0.GetColumn(i, "REQKIND") == "7"
					|| ds_mm031_0.GetColumn(i, "REQKIND") == "S") 
					|| ((ds_mm031_0.GetColumn(i, "REQKIND") == "2" || ds_mm031_0.GetColumn(i, "REQKIND") == "3" || ds_mm031_0.GetColumn(i, "REQKIND") == "4") 
		   && ds_mm031_0.GetColumn(i, "MMPR_DEL") == "X")) && length(ds_mm031_0.GetColumn(i, "QTNUM")) < 1)
		   || ( apgubn != "3" && ((ds_mm031_0.GetColumn(i, "BUSSI") == "1" && apgubn != "1") || (ds_mm031_0.GetColumn(i, "BUSSI") != "1" && apgubn == "1"))))  
		{
			able = false; 
		}
		if (!able)
		{
			gfn_showAlert("CW00071", "승인(청구번호 : "+ quote(ds_mm031_0.GetColumn(i,"REQNO"))+")");
			return;
		}
	}

	if (!gfn_showConfirm("CI00014","승인")) {
		return;
	}
	for (var i = 0; i < ds_mm031_0.rowcount ; i++)
	{
		ds_mm031_0.SetColumn(i,"GBN","C");
	}

	//TRACE(ds_mm031_0.SaveXML());

	tit_clearActionInfo();
	tit_addMultiActionInfo("cs80:CS8003001A_U2");	 // zcst001G 
	tit_addMultiActionInfo("cs80:CS8003001A_U14");   // ZCST003G 
	tit_addMultiActionInfo("cs80:CS8003001A_I2"); 	 // ZMMT001 

	tit_callService(
		 ""
		, ""
		, "ds_mm031_0=ds_mm031_0:A"
		, ""
		, ""
		, "fn_afterApproval"
		, ""
		, false
		, true);
}

/********************************************************************************
* 기      능   :  삭제 25
********************************************************************************/
function fn_delete() {
	//  삭제할 데이터셋을 복사
	gfn_copyDs(ds_mm031, ds_mm031_0, "CHECK=1");

	// 처리 데이터 확인
	if (ds_mm031_0.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}

	for (var i = 0; i < ds_mm031_0.rowcount ; i++)
	{
		if(ds_mm031_0.GetColumn(i,"RECE_F") != "0"){
			gfn_showAlert("CW00072");
			ds_mm031_0.SetColumn(i,"CHECK","0");
			return;
		}
		if(length(ds_mm031_0.GetColumn(i,"DOCNO")) > 0){
			gfn_showAlert("CW00064","결재문서");
			ds_mm031_0.SetColumn(i,"CHECK","0");
			return;
		}
	}

	// 청구취소 확인
	if (!gfn_showConfirm("CI00015","청구")) {
		return;
	}

	for (var i = 0; i < ds_mm031_0.rowcount ; i++)		//청구취소 상태로 세팅
	{
		ds_mm031_0.SetColumn(i,"GBN","E");
	}

	tit_clearActionInfo();
	tit_addMultiActionInfo("cs80:CS8003001A_U2");	// ZCST001G
	tit_addMultiActionInfo("cs80:CS8003001A_D1");   // ZMMT031

	tit_callService(
		 ""
		, ""
		, "ds_mm031_0=ds_mm031_0:A"
		, ""
		, ""
		, "fn_afterDelete"
		, "");

}
/********************************************************************************
* 기      능   : 취소 35
********************************************************************************/
function fn_cancel() {

	// 저장할 데이터셋을 복사
	gfn_copyDs(ds_mm031, ds_mm031_0, "CHECK=1");

	// 하나의 견적번호에 여러개의 문서번호가 존재하고
	// SAP 진행상태(프로젝트 생성)를 체크하여 진행중이면 취소되지 않도록 처리
	// 2018.07.25 Han J.H
	fn_cancel_chk();

	// 처리 데이터 확인
	if (ds_mm031_0.rowcount < 1) {
		gfn_showAlert("CW00013");
		return;
	}

	var user_grp = gfn_getUserGroup();
	var kind = "";

	if (user_grp == "40"){  // 자재담당 승인취소

		for (var i = 0; i < ds_mm031_0.rowcount ; i++)
		{
			if (i > 0 && kind != ds_mm031_0.GetColumn(i,"RECE_F"))
			{
				gfn_showAlert("CW00167");   // 청구 취소와 승인 취소를 동시에 할수 없다.
				return;
			}			
			if (ds_mm031_0.GetColumn(i,"RECE_F") == "1" || ds_mm031_0.GetColumn(i,"RECE_F") == "5")   // 전송취소처리
			{
				kind = ds_mm031_0.GetColumn(i,"RECE_F");

				ds_mm031_0.SetColumn(i,"GBN","E");
				if (!gfn_showConfirm("CI00013","전송취소")) {
					return;
				}
			}
			else if (ds_mm031_0.GetColumn(i,"RECE_F") == "2" || ds_mm031_0.GetColumn(i,"RECE_F") == "6") // 접수취소
			{
				kind = ds_mm031_0.GetColumn(i,"RECE_F");
				ds_mm031_0.SetColumn(i,"GBN","B");
				if (!gfn_showConfirm("CI00013","접수")) {
					return;
				}
			}
			else if (ds_mm031_0.GetColumn(i,"RECE_F") == "3") // 승인취소
			{
				kind = ds_mm031_0.GetColumn(i,"RECE_F");
				ds_list.fireevent = false;
				ds_list.GroupKey("REQNO,MATNR,BWTAR");
				if (ds_list.GetColumn(i,"STATE") != '1'){
					gfn_showAlert("CW00071", "승인취소(마감완료)");
					ds_list.GroupKey("REQNO,REQSN");
					ds_list.fireevent = true;
					return;
				}
				if (!gfn_showConfirm("CI00013","승인")) {
					return;
				}
				ds_mm031_0.SetColumn(i,"GBN","D");
			} 
			else
			{
				gfn_showAlert("CW00071", "작성/접수/승인 취소(상태확인)"); // 상태값이 없으면 오류
				return;
			}

		}

		tit_clearActionInfo();

       // 접수취소 ,승인취소
		tit_addMultiActionInfo("cs80:CS8003001A_U1"); // ZMMT031 
		tit_addMultiActionInfo("cs80:CS8003001A_U2"); // ZCST001G 

		if (kind == "3")  // 승인취소
		{
			tit_addMultiActionInfo("cs80:CS8003001A_U5"); // zmmt001 
		}

		tit_callService(
             ""
            , ""
			, "ds_mm031_0=ds_mm031_0:A"
			, ""
			,""
			, "fn_afterCancel"
			,""
			, ""
			,"");

	}

}

