/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 디테일 정보 필터링
	if (ds_mm031.rowcount > 0)
	{
		gfn_copyDs(ds_mm031, ds_mm031_2,);
		ds_list.filter("REQNO == " + quote( ds_mm031.GetColumn(0,"REQNO")));
	}

	if (ds_list.rowcount > 0)
	{
		gfn_copyDs(ds_list, ds_list_4);
	}

// 메시지 처리 
	gfn_showMsg("CI00002",ds_mm031.RowCount()+"");
}
/********************************************************************************
* 기      능   : 승인 Callback  
********************************************************************************/
function fn_afterApproval(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

function fn_afterSave(errCode, errMsg){
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

function fn_afterCancel(errCode, errMsg){
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

function fn_afterDelete(errCode, errMsg){
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}
