/********************************************************************************
* 기      능   : 자재청구 취소 검증로직
               : SAP 진행상태(프로젝트 생성)를 체크하여 취소되지 않도록 처리
               : 2018.07.25 Han J.H
********************************************************************************/
function fn_cancel_chk()
{
	tit_clearActionInfo();
	tit_addSearchActionInfo("cs80:CS8003001A_S3");

	tit_callService(
        ""
        , ""
        , "ds_mm031_0=ds_mm031_0:A"
        , "ds_cancel_chk=ds_cancel_chk"
        , ""
        , "fn_cancelChkAfterQuery"
        , true);
}

/********************************************************************************
* 기      능   : 자재청구 취소 검증로직 Callback
********************************************************************************/
function fn_cancelChkAfterQuery(errCode, errMsg) {
	for (var i = 0; i < ds_cancel_chk.rowcount ; i++) {
		if(ds_cancel_chk.GetColumn(i,"RSTAT") == "B" || ds_cancel_chk.GetColumn(i,"RSTAT") == "C") {
			gfn_showAlert("SAP 자재청구 승인이 완료되어 취소할수 없습니다.\n SAP 진행상태를 확인하여 주십시오.");
			return;
		}
	}	
}
