function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	var userGroup = gfn_getUserGroup();

	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS8003003A");
	ds_rcond.SetColumn(0, "GBN", "01");	
	allView_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();	

	// 2019.04.30 해외서비스팀 요청으로 중국법인 특정인원만 전체 조회로 보여주도록 변경
	// 공통권한 모듈 활용하여 관리자 권한이 아니면서 전체뷰 권한이 없을때 기존로직 수행함.
	if(userGroup != "40" && allView_auth != true ) {
		ufn_hideUnitComponents();
	}

	//2019.05.03 해외서비스 id중 zcst0999에서 권한 부여한 id 이지만 해당 id는 공급업체명 숨기기 : 박현씨 요청
	if ( G_USER_ID == "C000964" ) {
		grd_list.SetColProp(34,"width",0); // 공급업체명
	}

	// 마운트 지앤디 발주업체가 아니면 숨김처리.
	// 2019.03.12 해외서비스 요청으로 발주업체 보이도록 주석처리.
	/*if(!gfn_getAuthGroup("ZMM21")) { 
		gfn_hideGridColumn(grd_list,32); // 발주업체
	}*/	

	var codeArr = ["LAND1"];	
	var dsObjArr = [ds_land1_cd];
	var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

    // 청구종류코드 DataSet 가져오기
    var codeArr = ["3","CSH005"];
    var dsObjArr = [ds_reqkind_cd,ds_gubn01];
    var addTypeArr = [G_CODE_ALL,G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

	// 자재평가유형코드 DataSet 가져오기
    var codeArr = ["2"];
    var dsObjArr = [ds_bwtar_cd];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

    // 자재유형코드 DataSet 가져오기
    var codeArr = ["1", "AB_LYT_S"];
    var dsObjArr = [ds_mtart_cd, ds_layout];
    var addTypeArr = [G_CODE_NONE, G_CODE_NONE];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true); 

    //  Packing 진행상태 DataSet 가져오기

	// gfn_queryDD07T("ZCS_PACKING", ds_packing, "N");

    cal_budat_to.value = gfn_getCurrDate();
    cal_budat_fm.value = gfn_getBeforeDate(cal_budat_to.value, 7);

}
