/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	// 최대 12개월로 제한. 2019.03.12 Han J.H
	var diffMonth = gfn_getMonthCnt(cal_budat_fm.value, cal_budat_to.value);

	if(diffMonth > 12) {
		gfn_showAlert("조회기간의 경우 최대 12개월까지만 가능합니다.");
		return;
	}

	// 종료일자는 시작일자보다 이전일수 없음.
	if(cal_budat_to.value < cal_budat_fm.value) {
		gfn_showAlert("종료일자의 경우 시작일자보다 이전 일 수 없습니다.");
		return;
	}

	if(length(trim(ds_cond.GetColumn(0,"QTNAM"))) && length(trim(ds_cond.GetColumn(0,"QTNAM"))) < 10) {
		gfn_showAlert("견적명의 경우 최소 10글자 이상으로 입력해주세요.");
		ed_qtnam.SetFocus();
		return;
	}

	// 견적명 최대 50글자로 입력 제한되도록 Validation 검증로직 추가
	// 2020.10.16 Han J.H
	if(Length(Trim(ds_cond.GetColumn(0,"QTNAM"))) > 50) {
		var arr = [st_qtnam.Value, "51글자"];
		gfn_showAlert("CW00133", arr);
		return false;
	}

	// 자재내역 최소 자릿수 제한
	// 2020.10.19 Han J.H
	if(length(trim(ds_cond.GetColumn(0,"MAKTX"))) && length(trim(ds_cond.GetColumn(0,"MAKTX"))) < 3) {
		gfn_showAlert("자재내역의 경우 최소 3글자 이상으로 입력해주세요.");
		ed_maktx.SetFocus();
		return;
	}

	// 자재내역 최대 자릿수 제한
	// 2020.10.19 Han J.H
	if(Length(Trim(ds_cond.GetColumn(0,"MAKTX"))) > 40) {
		var arr = [st_maktx.Value, "41글자"];
		gfn_showAlert("CW00133", arr);
		return false;
	}

	ds_cond.SetColumn(0, "LAND1", trim(div_code.cb_mid.value));	
	ds_cond.SetColumn(0, "LIFNR", trim(div_code.cb_small.value));	

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	ds_list.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs80:CS8003003A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);
}

