/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() {

	ds_excel.ClearData();
	var j = 0;
	for(var i=0; i<ds_list.rowCount(); i++ ) {

		if(ds_list.getColumn(i, "CHECK") == true) {
			ds_excel.AddRow();
			ds_excel.CopyRow(j,ds_list,i);
			j++;
		}
	}

	if(ds_excel.rowcount() > 0) {
		// Excel Export 
		gfn_exportExcel(grd_excel,"PO STATUS");
	} else {
		// Excel Export 
		gfn_exportExcel(grd_list,"PO STATUS");
	}

}

