/********************************************************************************
* 기      능   : 저장  
********************************************************************************/
function fn_save() {
	ds_save.ClearData();

	if(!gfn_showConfirm("저장하시겠습니까?")) {
		return false;
	}

	var j = 0;
	for(var i=0; i<ds_list.rowCount(); i++ ) {

		if(ds_list.getColumn(i, "FLAG") == "U") {
			// 설계사양완료여부 저장시 값이 1이면 ''으로 데이터셋 세팅함. 2019.03.12 Han J.H
			if(ds_list.getColumn(i, "DESIGNSPECYN") == "1") {
				ds_list.SetColumn(i, "DESIGNSPECYN", "");
			}

			ds_save.AddRow();
			ds_save.CopyRow(j,ds_list,i);
			j++;
		}

	}

	tit_clearActionInfo();
	tit_addMultiActionInfo("cs80:CS8003003A_U1");   
	tit_callService(
		""
		, ""
		, "ds_save=ds_save"
		, ""
		, ""
		, "fn_afterSave");

}
function fn_afterSave() {

	fn_query();
}

