function chk_lifnr_filter_OnClick(obj,strValue)
{
	var filter_cond = "";
	if (chk_lifnr_filter.Value == 1) {
		filter_cond = ed_lifnr_nm.Text ;
		//trace(filter_cond);
		ds_list.Filter("LIFNRM_NM = '" + filter_cond+ "' OR LIFNRM = '" + filter_cond+ "'");
	} else {
		ds_list.UnFilter();
	}

}

