function ds_list_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if(nReason == 0) {
		for(var i=0; i<obj.rowCount(); i++ ) {

			if(obj.getColumn(i, "LAYOUT_S") != '') {
				var t = "status0" + obj.getColumn(i, "LAYOUT_S");
				obj.SetColumn(i,"IMG",t);
			}

			// 2018.12.14 이지원DL 요청. LT1,LT2,LT3 각 컬럼의 일수가 1 미만 일때는 빈값으로 표기함.
			if(obj.getColumn(i, "LT1") < 1) {
				obj.SetColumn(i,"LT1","");
			}

			if(obj.getColumn(i, "LT2") < 1) {
				obj.SetColumn(i,"LT2","");
			}

			if(obj.getColumn(i, "LT3") < 1) {
				obj.SetColumn(i,"LT3","");
			}

		}
	}
}

// 패킹의뢰일, 패킹일 지정 시 체크된 청구건에 대해 일괄 선택됨
// 2018.10.15 J.H Han
function btn_btn_con_packing_OnClick(obj)
{
	var packinggb = '';

	if(cb_packingdt.Value == '') {
		gfn_showAlert("입력값구분을 선택해 주시기 바랍니다.");
		cb_packingdt.SetFocus();
		return;
	//} else if(cb_packingdt.Value == '0') { // 포장의뢰일
	//	packinggb = 'PACKINGDT_RQ';
	} else if(cb_packingdt.Value == '0') { // 화성이송일
		packinggb = 'HSTSDT';	
	} else if(cb_packingdt.Value == '1') { // 포장일
		packinggb = 'PACKINGDT';
	} else if(cb_packingdt.Value == '2') { // 출하일
		packinggb = 'RELEASEDT';
	} else if(cb_packingdt.Value == '3') { // 사양확정일
		packinggb = 'SPECFIXDT';
	} else if(cb_packingdt.Value == '4') { // 지연입고예정일
		packinggb = 'YIPGOYDT_DLY';
	}


	if(ds_list.FindRow("CHECK", 1) == -1) {
		gfn_showAlert("처리할 자료를 먼저 선택해 주십시오.");
		return;
	}

	if(cal_packingdt.Value == '') {
		gfn_showAlert("날짜를 선택해 주시기 바랍니다.");
		return;
	}

	for(var i=0;i<ds_list.GetRowCount();i++) {

		if(ds_list.getColumn(i, "CHECK") == true) {

			if(packinggb == "SPECFIXDT") { // 사양확정일 일때
				var chkDesignSpec = 1; // 설계사양 기본값 True
				chkDesignSpec = ds_list.GetColumn(i, "DESIGNSPECYN"); // 설계사양

				if(chkDesignSpec == 1) {
					gfn_showAlert("설계사양 항목이 체크되어 있어 사양확정일 일자를 지정 할 수 없습니다.");
					return;					
				} else {
					ds_list.SetColumn(i, packinggb, cal_packingdt.Value);
				}

			} else if(packinggb == "YIPGOYDT_DLY") { // 지연입고예정일 일때
				var planDat = ds_list.GetColumn(i, "PLAN_DAT"); // 입고예정일

				if(planDat >= cal_packingdt.Value) { // 입고예정일보다 지연입고예정일이 이전으로 세팅됬다면 예외처리함.
					gfn_showAlert("지연입고예정일은 입고예정일보다 이전이거나 같을수 없습니다.");
					return;
				} else {
					ds_list.SetColumn(i, packinggb, cal_packingdt.Value);
				}
			} else {
				ds_list.SetColumn(i, packinggb, cal_packingdt.Value);
			}

		}
	}	
}

function btn_btn_sendmail_OnClick(obj)
{
	if(ds_list.FindRow("CHECK", 1) == -1) {
		gfn_showAlert("처리할 자료를 먼저 선택해 주십시오.");
		return;
	}

	var j = 0;
	var m_header = '';

	// 전체 체크건수 확인
	for(var i=0; i<ds_list.GetRowCount(); i++) {
		if(ds_list.getColumn(i, "CHECK") == true) {
			if(m_header == '') {
				m_header = ds_list.getColumn(i, "POSID");
			} else if(m_header != '') {
				m_header += " | " + ds_list.getColumn(i, "POSID");
			}
			j++;
		}
	}

	if(j < 6) { // 메일 발송의 경우 최대 5건으로 제한
		var arg = "fa_header=" + quote(m_header);
		gfn_openPopup("CS8003003C",true,arg);
	} else if(j >= 6){ // 5건 이상일시 보내지 못하도록 막음
		gfn_showAlert("메일 발송의 경우 최대 5건 까지 발송 가능합니다.");
		return;	
	}

}

function ds_list_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	// 사양확정일 유효성 검증. 2019.03.12 Han J.H
	if(strColumnID == 'SPECFIXDT') {
		if(varNewValue != '00000000') { // 초기화된 값이 아닌경우 유효성 검증로직 수행
			if(!gfn_dateValidChk(varNewValue)) {
				return false;
			}
		}
	}
}

// 검색조건 입력값구분 일자 초기화
// 2019.03.14 Han J.H
function btn_btn_con_reset_OnClick(obj)
{
	var packinggb = '';

	if(cb_packingdt.Value == '') {
		gfn_showAlert("입력값구분을 선택해 주시기 바랍니다.");
		cb_packingdt.SetFocus();
		return;
	//} else if(cb_packingdt.Value == '0') { // 포장의뢰일
	//	packinggb = 'PACKINGDT_RQ';
	} else if(cb_packingdt.Value == '0') { // 화성이송일
		packinggb = 'HSTSDT';	
	} else if(cb_packingdt.Value == '1') { // 포장일
		packinggb = 'PACKINGDT';
	} else if(cb_packingdt.Value == '2') { // 출하일
		packinggb = 'RELEASEDT';
	} else if(cb_packingdt.Value == '3') { // 사양확정일
		packinggb = 'SPECFIXDT';
	} else if(cb_packingdt.Value == '4') { // 지연입고예정일
		packinggb = 'YIPGOYDT_DLY';
	}

	if(ds_list.FindRow("CHECK", 1) == -1) {
		gfn_showAlert("처리할 자료를 먼저 선택해 주십시오.");
		return;
	}

	for(var i=0;i<ds_list.GetRowCount();i++) {
		if(ds_list.getColumn(i, "CHECK") == true) {
			var tmpVal = '';
			if(packinggb == 'RELEASEDT' || packinggb == 'YIPGOYDT_DLY') { // 출하일, 지연입고예정일
				tmpVal = '00000000';
				ds_list.SetColumn(i, packinggb, tmpVal);
			} else if(packinggb == 'SPECFIXDT') { // 사양확정일
				tmpVal = '00000000';
				ds_list.SetColumn(i, packinggb, tmpVal);
				ds_list.SetColumn(i, 'DESIGNSPECYN', 1); // 사양확정일 초기화 시 설계사양 체크박스를 기본값(1)으로 세팅. 즉 설계사양 CHK 되어 있으면 사양확정일은 Null 값을 의미함.
			} else {
				ds_list.SetColumn(i, packinggb, cal_packingdt.Value);
			}
		}
	}
}
/********************************************************************************
* 기      능   : 견적명 검색조건 항목을 활성/비활성
********************************************************************************/
function showQtnam(visible) {
	if(visible == true) {
		st_qtnam.Visible = true;
		ed_qtnam.Visible = true;	
	} else if(visible == false) {
		st_qtnam.Visible = false;
		ed_qtnam.Visible = false;	
	}
}
