function ufn_masterValidate(){
	if(gfn_isNull(ed_pjt, true, Static0.Value)) return false;
	//if(gfn_isNull(ed_zsitenm, true, Static4.Value)) return false;
	if(gfn_isNull(Edit1, true, Static11.Value)) return false;
	if(gfn_isNull(Combo1, true, Static14.Value)) return false;
	//if(gfn_isNull(cb_bsu, true, Static15.Value)) return false;
	//if(gfn_isNull(Edit2, true, Static1.Value)) return false;
	if(gfn_isNull(Combo4, true, Static2.Value)) return false;

	return true;
}
function ufn_detailValidate(){
	if(ds_list.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		// return;
	}

	for(var i=0; i<ds_list.GetRowCount(); i++){
		if(ds_list.GetColumn(i, "CHECK")==1){
			if(length(ds_list.GetColumn(i, "CS101_ABG"))<=0){
				gfn_showMsg("CW00001", i+" 행 빌딩구분");
				return false;
			}
			if(length(ds_list.GetColumn(i, "ZMUSAOCNT"))<=0){
				gfn_showMsg("CW00001", i+" 행 무상공사지급개월");
				return false;
			}
			if(length(ds_list.GetColumn(i, "ZMUSAOCNT2"))<=0){
				gfn_showMsg("CW00001", i+" 행 무상일반지급개월");
				return false;
			}
			if(length(ds_list.GetColumn(i, "ZSPEC12"))<=0){
				gfn_showMsg("CW00001", i+" 행 기종");
				return false;
			}
			if(length(ds_list.GetColumn(i, "ZSPEC2"))<=0){
				gfn_showMsg("CW00001", i+" 행 사양");
				return false;
			}
			if(length(ds_list.GetColumn(i, "ZSPEC3"))<=0){
				gfn_showMsg("CW00001", i+" 행 속도");
				return false;
			}
			if(length(ds_list.GetColumn(i, "ZSPEC6"))<=0){
				gfn_showMsg("CW00001", i+" 행 용량");
				return false;
			}
			if(length(ds_list.GetColumn(i, "ZSPEC9"))<=0){
				gfn_showMsg("CW00001", i+" 행 주차대수");
				return false;
			}
			if(length(ds_list.GetColumn(i, "ZSPEC10"))<=0){
				gfn_showMsg("CW00001", i+" 행 PSD 도어수");
				return false;
			}

			// detail 항목 setting

			ds_list.SetColumn(i, "CS101_AGB", Combo4.Value);
		}
	}
	return true;
}
function fn_save() {
	// 물류 분사 PSD 트랜젝션 제어 20170831 Start*****//
	if (substr(ed_pjt.Text,0,1) == "G" ||substr(ds_list.GetColumn(0, "CS101_HNO"),0,1) == "G") {	
		gfn_showAlert("CW00246");
		return;
	}
	// 물류 분사 PSD 트랜젝션 제어 20170831 End*******//	

	// master validation
	if(!ufn_masterValidate()){
		return;
	}
	// 임유진에 한해서 필수 체크 제외 
	if(  G_USER_ID != "2021153") {
			// detail validation
		if(!ufn_detailValidate()){
			return;
		}
	}	

	for(var i=0; i<ds_list.RowCount(); i++) {					
		if(ds_list.GetColumn(i, "CHECK") == true) {
			var hno =  ds_list.GetColumn(i, "CS101_HNO") ;

			if(length(trim(ds_list.GetColumn(i, "SID"))) != 0){
				if(length(trim(ds_list.GetColumn(i, "SID"))) < 7) {
					gfn_showAlert(hno + " 의 승강기 번호는 숫자 7자리로 입력이 가능합니다.");
					return;
				}
			}
		}
	}

	ds_list.Filter("CHECK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

	tit_clearActionInfo();
	if(ds_list.FindRow("CHECK", 1)==-1){
		tit_addMultiActionInfo("cs96:CS9606001A_U1");   // zmaster01
		tit_addMultiActionInfo("cs96:CS9606001A_U2");   // zmaster02 변경일자 
		tit_addMultiActionInfo("cs96:CS9606001A_U4");   // zcst101
		tit_addMultiActionInfo("cs96:CS9606001A_U6");   // zcst111
		tit_addMultiActionInfo("cs96:CS9606001A_U12");   // zcst116 
		tit_addMultiActionInfo("cs96:CS9606001A_U13");   // zcst126 
		tit_addMultiActionInfo("cs96:CS9606001A_U14");   // zcst131 
		tit_addMultiActionInfo("cs96:CS9606001A_U15");   // zcst136 
		tit_addMultiActionInfo("cs96:CS9606001A_U16");   // zcst121 
	} else {
		tit_addMultiActionInfo("cs96:CS9606001A_U1");   // zmaster01
		tit_addMultiActionInfo("cs96:CS9606001A_U2");   // zmaster02 변경일자 
		tit_addMultiActionInfo("cs96:CS9606001A_U3");   // zmaster02 사양변경 
		tit_addMultiActionInfo("cs96:CS9606001A_U4");   // zcst101
		tit_addMultiActionInfo("cs96:CS9606001A_U5");   // zcst101
		tit_addMultiActionInfo("cs96:CS9606001A_U6");   // zcst111 
		tit_addMultiActionInfo("cs96:CS9606001A_U7");   // zcst111
		tit_addMultiActionInfo("cs96:CS9606001A_U8");   // zcst116 
		tit_addMultiActionInfo("cs96:CS9606001A_U9");   // zcst126 
		tit_addMultiActionInfo("cs96:CS9606001A_U10");   // zcst131 
		tit_addMultiActionInfo("cs96:CS9606001A_U11");   // zcst136 
		tit_addMultiActionInfo("cs96:CS9606001A_U17");   // zcst111 - M 프로젝트 생성사유 변경 
	}

	tit_callService(
		""
		, ""
		, "ds_master=ds_master ds_list=ds_list2"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}
