/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리
	gfn_showSearchMsg(grd_list);

	// trace(ds_list.SaveXML());
	// trace(ds_list2.SaveXML());

	ds_combo3.Filter("AREA == " + quote(Combo1.Value));

	//cb_bsu_OnChanged("",cb_bsu.value);

	if( ds_list.RowCount() > 0 ) {
		// 전략마케팅팀
		if(gfn_getAuthGroup("ZCS07")){
			grd_list.SetCellProp("body",22,"Edit","combo");	// 빌딩구분 
			if(substr(ds_list.GetColumn(0,"CS101_PJT"), 0, 1) == "M") {
				grd_list.SetCellProp("body",26,"Edit","combo");	// 생성사유
			} else {
				grd_list.SetCellProp("body",26,"Edit","none");	// 생성사유
			}

			grd_list.SetCellProp("body",10,"Edit","combo");	// 기종변경
		} else {
			// 빌딩구분 변경 불가 	
			grd_list.SetCellProp("body",22,"Edit","none");	// 빌딩구분 
			grd_list.SetCellProp("body",26,"Edit","none");	// 생성사유

			grd_list.SetCellProp("body",10,"Edit","none");	// 기종변경
		}	
	}

}
/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( ds_error.rowcount() > 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
	}
	 fn_query();

}
/********************************************************************************
* 기      능   : 삭제 Callback
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if ( ds_error.rowcount() > 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_OnLoadCompleted('');
	}
}
