function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리
		gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	// ds_list.fireevent = false;

	if(strColumnID!="CHECK"){
		ds_list.SetColumn(nRow, "CHECK", 1);
	}

	var hnos = left(ds_list.GetColumn(nRow, "CS101_HNO"),1);
	// 빌딩구분
	if(strColumnID == "CS101_ABG"){
		if(hnos=="L" || hnos=="S" || hnos=="W"){
			if(varNewValue=="C" || varNewValue=="D" || varNewValue=="E"){
//			Trace(strColumnID+"||"+varNewValue+"||"+varOldValue);
				// Trace("아파트와 빌딩만 선택 가능합니다.");
				gfn_showAlert("CW00124","아파트와 빌딩만");
				obj.fireevent = false;
				ds_list.SetColumn(nRow, "CS101_ABG", varOldValue);
				obj.fireevent = true;
				return;
			} else {	
				gfn_clearMsgBar();
			}
		} else if(hnos=="G") {
			if(varNewValue!="C"){
				// Trace("PSD만 선택 가능");
				gfn_showAlert("CW00124","PSD만");
				obj.fireevent = false;
				ds_list.SetColumn(nRow, "CS101_ABG", varOldValue);
				obj.fireevent = true;
				return;
			} else {	
				gfn_clearMsgBar();
			}
		} /*else if(hnos=="J") {
			if(varNewValue!="D"){
				// Trace("주차만 선택 가능");
				gfn_showAlert("CW00124","주차만");
				obj.fireevent = false;
				ds_list.SetColumn(nRow, "CS101_ABG", varOldValue);
				obj.fireevent = true;
				return;
			} else {	
				gfn_clearMsgBar();
			}
		}*/
	}

	// 주정검자, 부정검자
	if(strColumnID=="JUJ" || strColumnID=="BUJ"){
		if(ds_list.GetColumn(nRow, "JUJ") == ds_list.GetColumn(nRow, "BUJ")){
			// Trace("주점검자, 부점검자 동인인은 입력할 수 없습니다.");
			gfn_showAlert("CW00125");
			ds_list.SetColumn(nRow, strColumnID, varOldValue);
			return;
		}
	}

	var arrMsg;

	// 무상공사발주개월
	if(strColumnID=="ZMUSAUCNT" || strColumnID=="ZMUSAOCNT"){
		if(ds_list.GetColumn(nRow, "ZMUSAOCNT") < ds_list.GetColumn(nRow, "ZMUSAUCNT")){
			//Trace("무상공사지급개월은 무상공사발주개월보다 작을 수 없습니다.");
			arrMsg = [nRow, "무상공사지급개월", "무상공사발주개월"];
			gfn_showAlert("CW00126", arrMsg);
			ds_list.SetColumn(nRow, strColumnID, varOldValue);
			return;
		}
	}

	// 무상일반발주개월
	if(strColumnID=="ZMUSAUCNT2" || strColumnID=="ZMUSAOCNT2"){
		if(ds_list.GetColumn(nRow, "ZMUSAOCNT2") < ds_list.GetColumn(nRow, "ZMUSAUCNT2")){
			//Trace("무상일반지급개월은 무상일반발주개월보다 작을 수 없습니다.");
			arrMsg = [nRow, "무상일반지급개월", "무상일반발주개월"];
			gfn_showAlert("CW00126", arrMsg);
			ds_list.SetColumn(nRow, strColumnID, varOldValue);
			return;
		}
	}

	obj.fireevent = false;
	if(strColumnID=="CS101_BSU") {
		fn_change_bsu_cd(ds_list.GetColumn(nRow, "CS101_BSU"));
		fn_change_bpm_cd(ds_list.GetColumn(nRow, "CS101_BSU"));
	}
	obj.fireevent = true;

	// ds_list.fireevent = true;
}
function setEnableCols(nRow, col, ret, hno, gbn){
	var hnos = left(ds_list.GetColumn(nRow, "CS101_HNO"),1);

	if(hnos == hno){
		if(gbn == "T"){
			return ret;
		} else {
			return "none";
		}
	} else {
		if(gbn == "F"){
			return ret;
		} else {
			return "none";
		}
	}
}
