var curCell;
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);

	cal_temp.top = rect[1]+80;
	cal_temp.Left = rect[0]+91;
	cal_temp.Visible = true;

	if(nCell==4){
		cal_temp.value = ds_list.getColumn(nRow, "CS101_FDT");
	} else if(nCell==5) {
		cal_temp.value = ds_list.getColumn(nRow, "IJD");
	}
	curCell = nCell;
	cal_temp.SetFocus();
}
/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgBar();

	if(curCell==4){
		ds_list.setColumn(ds_list.row, "CS101_FDT", strText);
	} else {
		ds_list.setColumn(ds_list.row, "IJD", strText);
	}

	obj.Visible = false;
}
/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우
********************************************************************************/
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
}

