function fn_change_bsu_cd(bsu_cd)
{
	ds_grd_combo1.filter("COD == " + quote(bsu_cd));
	ds_grd_combo2.filter("COD == " + quote(bsu_cd));
}

function fn_change_bpm_cd(bsu_cd)
{
	ds_combo6.filter("COD == " + quote(bsu_cd));
}

function fn_change_cod(currow)
{
	return "COD=" + quote(ds_list.GetColumn(currow,"CS101_BSU"));
}

function fn_change_code(currow)
{
	return "COD=" + quote(ds_list.GetColumn(currow,"CS101_BSU"));
}

