/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	var user_grp = gfn_getUserGroup();

	if(user_grp == '40') {
		if(gfn_isNull(div_code.cb_big, true, st_bsu.value))
			return;
	}

//=================================기존소스==============================

	if(length(ed_dipbxnumb.Text)  > 0 and length(ed_dipbxnumb.Text)  < 4 ) {
		gfn_showAlert(" 최소 4자 입력이 필수 입니다.");
		return;
	}

//=================================변경소스==============================
/* ===================최인식 차장 요청으로 원복 ====================
	if( length(ed_pjt.text) <= 0 ) 
	{
		if( length(ed_dipbxnumb.Text)  < 4  )
		{
			gfn_showAlert(" DIPBX번호 최소 4자 또는 원PJT 6자리 입력이 필수 입니다.");
			return;
		}

	}
===============================================*/	
//========================================================================

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else if(div_code.cb_big.value == '4') { // 협력사 콤보1이 부서일 경우
			ds_cond.SetColumn(0, "DPT", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_small.value));
			ds_cond.SetColumn(0, "BSU", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}

	if(chk_fail.Value == true) {
		ds_cond.SetColumn(0, "FAIL", "Y");
	} else {
		ds_cond.SetColumn(0, "FAIL", "");
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs96:CS9606003A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}

function fn_save() {
	gfn_clearMsgBar();

    if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showMsg("CW00013");
		return;
	}

	ds_list2.ClearData();
	ds_list3.ClearData(); // zcst118 데이타 셋 

	var j = 0;
	var k = 0;
	for(var i=0; i<ds_list.RowCount(); i++) {					
		if(ds_list.GetColumn(i, "CHK") == true) {
			// 안현우 사원 요청 필수 체크 해제 20160315
			// if(length(trim(ds_list.GetColumn(i, "SID"))) == 0 && length(trim(ds_list.GetColumn(i, "MSU"))) == 0 ) {
				// gfn_showAlert("승강기번호를 입력하십시요.");
				// gfn_showAlert("승강기번호 또는 미입력 사유를 입력하십시요.");
				// return;
			// }

			var pjthno = ds_list.GetColumn(i, "PJT") + ds_list.GetColumn(i, "HNO") ;

			if(length(trim(ds_list.GetColumn(i, "SID"))) != 0){
				if(length(trim(ds_list.GetColumn(i, "SID"))) < 7) {
					gfn_showAlert(pjthno + " 호기의 승강기 번호는 숫자 7자리로 입력이 가능합니다.");
					return;
				}
			}

			ds_list2.AddRow();
			ds_list2.SetColumn(j, "FLAG", "U");
			ds_list2.SetColumn(j, "PJT", ds_list.GetColumn(i, "PJT"));
			ds_list2.SetColumn(j, "HNO", ds_list.GetColumn(i, "HNO"));
			ds_list2.SetColumn(j, "SID", ds_list.GetColumn(i, "SID"));
			ds_list2.SetColumn(j, "RGD", ds_list.GetColumn(i, "RGD"));
			ds_list2.SetColumn(j, "MSU", ds_list.GetColumn(i, "MSU"));

			var new_dipbxnumb = ds_list.GetColumn(i, "DIPBXNUMB");

			if( length(new_dipbxnumb) < 9 ) {
				gfn_showAlert(pjthno + " 호기의 비상통화장치 번호는 9자리 미만으로 입력이 불가능합니다. ");
				return;
			}
			if( length(new_dipbxnumb) > 11 ) {
				gfn_showAlert(pjthno + " 호기의 비상통화장치 번호는 11자리 이하로 입력바랍니다. ");
				return;	
			}

			// 비상통화장치 전화 번호 저장하기	 20150609 이하림		
			ds_list2.SetColumn(j, "DIPBXNUMB", ds_list.GetColumn(i, "DIPBXNUMB"));

			// 현장별 추가 정보 관리 20170928 이하림 
			if(ds_list.GetColumn(i, "REMARK") <> ds_list.GetOrgColumn(i, "REMARK")) {
				ds_list3.AddRow();
				ds_list3.CopyRow(k,ds_list,i);
				ds_list3.SetColumn(k, "FLAG", "U");
				k++;
			}

			j++;
		}
	}


	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""
		, "cs96:CS9606003A_U1"
		, ""
		, "FLAG");

	// zcst118 
	if(ds_list3.RowCount() > 0) {
		tit_addSaveActionInfo(
			""
			, "cs96:CS9606003A_U2"
			, ""
			, "FLAG");

	}

	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2 ds_list3=ds_list3"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

