var modem_auth01 = false; // 모뎀교체현황 해지/취소/서비스담당자권한 문구 활성화 권한
var modem_auth02 = false; // 모뎀교체현황 프로젝트맵핑/기성지급여부/DIPBX및HRTS 작업완료 체크박스 활성화 권한
var modem_auth03 = false; // 모뎀교체현황 DH인건비지급 체크박스 활성화 권한
