function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);
	gfn_initDs(ds_cond, true);

   	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS9606003B");
	ds_rcond.SetColumn(0, "GBN", "01");	
	modem_auth01 = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();

   	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS9606003B");
	ds_rcond.SetColumn(0, "GBN", "02");	
	modem_auth02 = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();	

   	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS9606003B");
	ds_rcond.SetColumn(0, "GBN", "03");	
	modem_auth03 = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();	

	var codeArr = ["VKGRP","VKBUR","CSHBOARD"];
    var dsObjArr = [ds_vkgrp,ds_vkbur,ds_hboard];
    var addTypeArr = [G_CODE_NONE,G_CODE_NONE,G_CODE_SELECT];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr, true);

}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {

	//if(G_USER_ID == "2021200" || G_USER_ID == "2023687" || G_USER_ID == "2020971"|| G_USER_ID == "2021056"|| 
	//   G_USER_ID == "2017938" || G_USER_ID == "2018900" || G_USER_ID == "2029758") {
	if(modem_auth01 == true) {// 모뎀교체현황 해지/취소/서비스담당자권한 문구 활성화 권한
		btn_cancel.Visible = true;
		btn_cancelC.Visible = true;
		cb_gbn.Visible = true;
		cb_gbn.Index = 0;
		st_admin.Visible = true; 

		//if(G_USER_ID == "2029758" || G_USER_ID == "2021056"){
		if(modem_auth02 == true) { // 모뎀교체현황 프로젝트맵핑/기성지급여부/DIPBX및HRTS 작업완료 체크박스 활성화 권한
			btn_pjtmap.Visible = true;
			cb_kisung.Visible = true;
			grd_list.SetCellProp("body",24,"edit","checkbox");
			grd_list.SetCellProp("body",25,"edit","checkbox");
		}
		//if(G_USER_ID == "2029758" || G_USER_ID == "2020971"){
		if(modem_auth03 == true) { // 모뎀교체현황 DH인건비지급 체크박스 활성화 권한
			grd_list.SetCellProp("body",26,"edit","checkbox");
			grd_list.SetCellProp("body",27,"edit","checkbox");
		}
	}
	else{
		grd_list.SetColProp(24,"width",0);
		grd_list.SetColProp(25,"width",0);
		grd_list.SetColProp(26,"width",0);
		grd_list.SetColProp(27,"width",0);
	}

}

