/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	var user_grp = gfn_getUserGroup();

	if(user_grp == '40') {
		if(gfn_isNull(div_code.cb_big, true, st_bsu.value))
			return;
	}

	//if (length(trim(div_code.cb_small.value)) == 0)
	if ( chk_cancel.Value != '1' and chk_target.Value != '1')
	{

		if (length(trim(ed_pjt.value)) < 4)
		{
			gfn_showAlert("'원PJT번호' 최소4글자 이상 또는 '해지완료/해지대상'의 조회값을 입력해주십시오.");
			ed_pjt.SetFocus();		
			return;
		}	
	}


	ds_cond.SetColumn(0, "LCOST", cb_kisung.Value); //기성지급여부 구분
	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else if(div_code.cb_big.value == '4') { // 협력사 콤보1이 부서일 경우
			ds_cond.SetColumn(0, "DPT", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_small.value));
			ds_cond.SetColumn(0, "BSU", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "DPT", '');
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs96:CS9606003B_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}

function fn_save() {
	gfn_clearMsgBar();

    if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showAlert("CW00013");
		return;
	}

	ds_list2.ClearData();

	var j = 0;
	var h = 0;
	var pjthno, dipbxA, dipbxB, hrtsA, hrtsB, dat;
	for(var i=0; i<ds_list.RowCount(); i++) {					
		if(ds_list.GetColumn(i, "CHK") == true) {

			pjthno = ds_list.GetColumn(i, "PJT") + ds_list.GetColumn(i, "HNO") ;
			dipbxA = length(ds_list.GetColumn(i, "CS411_DIPBXA")) ;
			dipbxB = length(ds_list.GetColumn(i, "CS411_DIPBXB")) ;
			ddat   = length(ds_list.GetColumn(i, "CS411_DDAT")) ;	
			hrtsA  = length(ds_list.GetColumn(i, "CS411_HRTSA")) ;		
			hrtsB  = length(ds_list.GetColumn(i, "CS411_HRTSB")) ;	
			hdat   = length(ds_list.GetColumn(i, "CS411_HDAT")) ;	
			worker = length(ds_list.GetColumn(i, "CS411_WKER")) ;	
			board  = length(ds_list.GetColumn(i, "CS411_HBOARD")) ;


			if(dipbxA != 0 and dipbxA != 11){	
				gfn_showAlert(pjthno + " 호기의 비상통화장치 번호(전)는 11자리로 입력해주세요.");
				return;
			}
			if(dipbxB != 0 and dipbxB != 11){	
				gfn_showAlert(pjthno + " 호기의 비상통화장치 번호(후)는 11자리로 입력해주세요.");
				return;
			}
			if(hrtsA != 0 and hrtsA != 11){	
				gfn_showAlert(pjthno + " 호기의 HRTS 번호(전)는 11자리로 입력해주세요.");
				return;
			}
			if(hrtsB != 0 and hrtsB != 11){	
				gfn_showAlert(pjthno + " 호기의 HRTS 번호(후)는 11자리로 입력해주세요.");
				return;
			}

			if( (dipbxA == 0 and dipbxB != 0) or (dipbxA != 0 and dipbxB == 0) ) {			
				gfn_showAlert(pjthno + " 호기의 비상통화장치 변경 전/후 필수 항목입니다.");
				return;
			}

			if( (hrtsA == 0 and hrtsB != 0) or (hrtsA != 0 and hrtsB == 0) ) {			
				gfn_showAlert(pjthno + " 호기의 HRTS 변경 전/후 필수 항목입니다.");
				return;
			}
			// if(dipbxA + dipbxB + hrtsA + hrtsB == 0){			
				// gfn_showAlert(pjthno + " 호기의 모뎀번호를 입력하십시오.");
				// return;
			// }

			if( (dipbxA + dipbxB != 0) and ddat == 0 ) {			
				gfn_showAlert(pjthno + " 호기의 비상통화장치 작업완료일은 필수 항목입니다.");
				return;
			}
			if( (hrtsA + hrtsB != 0) and hdat == 0 ) {			
				gfn_showAlert(pjthno + " 호기의 HRTS 작업완료일은 필수 항목입니다.");
				return;
			}			

			if( (hrtsA + hrtsB != 0) and board == 0 ) {			
				gfn_showAlert(pjthno + " 호기의 보드타입은 필수 항목입니다.");
				return;
			}

			if( worker == 0 ) {			
				gfn_showAlert(pjthno + " 호기의 작업자명은 필수 항목입니다.");
				return;
			}

			if(ds_list.GetColumn(i, "CS411_HCHK") == "1"){
				if(ds_list.GetColumn(i, "CS411_HCAN") == 0) {
					gfn_showAlert(pjthno + " 호기는 해지완료 대상이 아닙니다.");
					return;
				}
				if(ds_list.GetColumn(i, "CS411_HWBS") == 0) {			
					gfn_showAlert(pjthno + " 호기의 HRTS 매핑 WBS가 누락되었습니다.");
					return;
				}
			}
			if(ds_list.GetColumn(i, "CS411_DCHK") == "1"){
				if(ds_list.GetColumn(i, "CS411_DCAN") == 0) {
					gfn_showAlert(pjthno + " 호기는 해지완료 대상이 아닙니다.");
					return;
				}
				if(ds_list.GetColumn(i, "CS411_DWBS") == 0) {			
					gfn_showAlert(pjthno + " 호기의 DI-PBX 매핑 WBS가 누락되었습니다.");
					return;
				}
			}


			ds_list2.AddRow();
			ds_list2.CopyRow(j,ds_list,i);

			if(ds_list.GetColumn(i, "SFLAG") != '183') {
				ds_list2.SetColumn(j, "FLAG", "I");
			} else {
				ds_list2.SetColumn(j, "FLAG", ds_list.GetColumn(i, "FLAG"));
			}
			// dipbxB  hrtsB 의 값이 같은 경우

			if(ds_list.GetColumn(i, "CS411_DIPBXB") == ds_list.GetColumn(i, "CS411_HRTSB") && (dipbxB + hrtsB) > 0) {
				ds_list2.SetColumn(j, "CS411_EQA", "Y");
				ds_list2.SetColumn(j, "CS411_HBOARD", "HYBRID3");
			} else {
				ds_list2.SetColumn(j, "CS411_EQA", "");
			}
			// dipbxB 비통변경후 번호를 zcst111 비통번호에 업데이트 한다.
			if(dipbxB != 0) {
				ds_list3.AddRow();
				ds_list3.CopyRow(h,ds_list,i);
				h++;
			}

			j++;
		}
	}

// 이미지 업로드 
// 파일 신규 등록 및 변경된 경우
	ds_list4.ClearData();
	var p = 0;

	for(var i=0; i<ds_list.RowCount(); i++) {					
		if(ds_list.GetColumn(i, "CHK") == true) {
			var fileName = ds_list.GetColumn(i, "FNAM");		// 로컬파일명
			var fileName_o = ds_list.GetOrgColumn(i, "FNAM");	// 이전 파일명
			//var posid = ds_list.GetColumn(i, "PJT") + ds_list.GetColumn(i, "HNO");
			var fileName2 = "CS9606003B_" + left(GetDate(),8) + "_" + fileName ; // 서버저장이름

			if(length(fileName) > 0 and fileName_o <> fileName) {

				var arr = tit_fileWrite(httpFile        //http객체
						, "CS"                  //저장디렉토리
						, fileDialog.FilePath   //로컬경로
						, fileName   			//로컬파일명
						, fileName2             // 실제 db에 저장될 파일 이름
						, "U"	                // time stamp를 붙이지 않을 경우 U 사용 
						, fileName_o);          //삭제할 이전 파일명 

				if ( arr[0] == "S" ) {	
					ds_list4.AddRow();
					ds_list4.CopyRow(p,ds_list,i);
					ds_list4.SetColumn(p, "FLAG", "U");
					ds_list4.SetColumn(p, "FNAM", arr[2]); // 서버 저장이름으로 저장

					p++;

				} 
			}		
		}
	}

	tit_clearActionInfo();
	// ZCST411 
	tit_addSaveActionInfo(
        "cs96:CS9606003B_I1",
        "cs96:CS9606003B_U1",
        "",
        "FLAG",
        "",
        0,
        "");
	// ZCST111 비통번호 UPDATE
	tit_addSaveActionInfo(
		""
		, "cs96:CS9606003B_U2"
		, ""
		, "FLAG");

	// 확인서 파일명 UPDATE 
	tit_addSaveActionInfo(
		""
		, "cs96:CS9606003B_U3"
		, ""
		, "FLAG");

	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2 ds_list3=ds_list3 ds_list4=ds_list4"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

