/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if(nCell == 1) {
		// 그리드 표시할 컬럼 선택
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리
		gfn_sortGrid(obj,nCell);
	}
}

function btn_popProj_OnClick(obj) {
	var arg = "pjt="+quote(ed_pjt.Text);
	gfn_openPopup("CS9601002A", true, arg);
}

/********************************************************************************
* 기      능   : Calendar 관련 
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex) {

 	var rect = obj.GetCellRect(nRow, ncell);

	var nX = ClientToScreenX(obj,rect[0]);
	var nY = ClientToScreenY(obj,rect[1] + 20);

	var dipbxa = length(ds_list.GetColumn(nRow, "CS411_DIPBXA")); 
	var dipbxb = length(ds_list.GetColumn(nRow, "CS411_DIPBXB")); 
	var hrtsa  = length(ds_list.GetColumn(nRow, "CS411_HRTSA")); 
	var hrtsb  = length(ds_list.GetColumn(nRow, "CS411_HRTSB")); 
	var hCan   = length(ds_list.GetColumn(nRow, "CS411_HCAN")); 
	var dCan   = length(ds_list.GetColumn(nRow, "CS411_DCAN")); 

	if(nCell == 14)  {		
		if(dCan == 0){
			if(dipbxa + dipbxb > 0 ) {
				PopupDiv0.TrackPopup(nX,nY);
				PopupDiv0.cal_temp.value = ds_list.GetColumn(nRow, "CS411_DDAT"); 
			} else {

				gfn_showAlert("비상통화장치 작업완료일은 변경 번호 입력 후 지정이 가능합니다.");
				return false;
			}	
		}
	 } 
	if(nCell == 19)  {		
		if(hCan == 0){	
			if(hrtsa + hrtsb > 0  and hCan == 0) {
				PopupDiv0.TrackPopup(nX,nY);
				PopupDiv0.cal_temp.value = ds_list.GetColumn(nRow, "CS411_HDAT"); 
			} else {

				gfn_showAlert("HRTS 작업완료일은 변경 번호 입력 후 지정이 가능합니다.");
				return false;
			}
		}
	 }  
	 PopupDiv0.cal_temp.SetFocus();

	// 확인서 업로드 
	if(nCell == 28) {
		btn_openFile_OnClick(obj);
	}
}

function PopupDiv0_cal_temp_OnDayClick(obj,strText) {

	if(grd_list.GetClickedCell() == 14) {
		ds_list.SetColumn(ds_list.row, "CS411_DDAT", strText);
	}
	if(grd_list.GetClickedCell() == 19) {
		ds_list.SetColumn(ds_list.row, "CS411_HDAT", strText);
	}
	PopupDiv0.ClosePopup();
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	gfn_changeFlag( obj, strColumnID, nRow, "FLAG" );

	var dipbxa = length(ds_list.GetColumn(nRow, "CS411_DIPBXA")); 
	var dipbxb = length(ds_list.GetColumn(nRow, "CS411_DIPBXB")); 
	var hrtsa = length(ds_list.GetColumn(nRow, "CS411_HRTSA")); 
	var hrtsb = length(ds_list.GetColumn(nRow, "CS411_HRTSB")); 

	if(strColumnID == "CS411_DIPBXA" or strColumnID == "CS411_DIPBXB" ){ 

		if( dipbxa + dipbxb == 0 ) {
			ds_list.SetColumn(nRow, "CS411_DDAT","");
		}
	}
	if(strColumnID == "CS411_HRTSA" or strColumnID == "CS411_HRTSB" ){ 

		if( hrtsa + hrtsb == 0 ) {
			ds_list.SetColumn(nRow, "CS411_HBOARD","");
		}
		if( hrtsa + hrtsb == 0 ) {
			ds_list.SetColumn(nRow, "CS411_HDAT","");
		}
	}

}

function btn_excel_OnClick(obj)
{	
	grd_list.ExportExcelEx("모뎀교체현황");
}

function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var hrtsa = length(ds_list.GetColumn(nRow, "CS411_HRTSA")); 
	var hrtsb = length(ds_list.GetColumn(nRow, "CS411_HRTSB")); 

	if(nCell == 18){ // 보드타입
		if( hrtsa + hrtsb == 0 ) {
			ds_list.SetColumn(nRow, "CS411_HBOARD","");
			gfn_showAlert("hrts 변경 번호 입력 후 입력해주세요.");
			grd_list.SetCellProp("body",18,"edit","none");
			return false;

		} else {
			grd_list.SetCellProp("body",18,"edit","combo");
		}
	}

	// 모뎀번호 유효성 체크  DIPBX
	if(nCell == 12 || nCell == 13) { 

		var arg = "i=" + quote(nRow)
		        + " p_gbnHD="     + quote("D")
		        + " p_modemNumA=" + quote(ds_list.GetColumn(nRow, "CS411_DIPBXA"))
		        + " p_modemNumB=" + quote(ds_list.GetColumn(nRow, "CS411_DIPBXB"))
		        + " p_dCan="      + quote(ds_list.GetColumn(nRow, "CS411_DCAN")) 
		        ;

		var rtn = gfn_openPopup("CS9606003D", true, arg);

		if(length(rtn) != 0){
			ds_list.setColumn(nRow, "CS411_DIPBXA", rtn[0]);
			ds_list.setColumn(nRow, "CS411_DIPBXB", rtn[1]);
		}
	}
	// 모뎀번호 유효성 체크  HRTS
	if(nCell == 17 || nCell == 16) { 

		var arg = "i=" + quote(nRow)
		        + " p_gbnHD="     + quote("H")
		        + " p_modemNumA=" + quote(ds_list.GetColumn(nRow, "CS411_HRTSA"))
		        + " p_modemNumB=" + quote(ds_list.GetColumn(nRow, "CS411_HRTSB"))
		        + " p_dCan="      + quote(ds_list.GetColumn(nRow, "CS411_HCAN")) 
		        ;

		var rtn = gfn_openPopup("CS9606003D", true, arg);

		if(length(rtn) != 0){
			ds_list.setColumn(nRow, "CS411_HRTSA", rtn[0]);
			ds_list.setColumn(nRow, "CS411_HRTSB", rtn[1]);
		}
	}

}

function btn_report_OnClick(obj)
{
	ds_list2.ClearData();
	var j = 0;
	for(var i=0; i<ds_list.RowCount(); i++) {
		if(ds_list.GetColumn(i, "CHK") == true) {
			ds_list2.AddRow();
			ds_list2.CopyRow(j,ds_list,i);
			ds_list2.SetColumn(j,"VKGRP_NM",ds_vkgrp.GetColumn(ds_vkgrp.FindRow("CODE",ds_list.GetColumn(i,"VKGRP")),"CODE_NAME"));

			j++;
		}
	}
	Dialog("CS96::CS9606003C.xml", "");
}

var F_SIZE = 1024 * 10096;
function btn_openFile_OnClick(obj)
{
	fileDialog.open();

	if ( length(fileDialog.FilePath) == 0 
		|| length( fileDialog.FileName ) == 0 ) {
		return;
	}

	fileSize = f_tmp.GetLength(fileDialog.FilePath + "\\" + fileDialog.FileName );
	if ( fileSize <= 0 ) {
		gfn_showAlert("CW00048");
		return;
	}

	if ( fileSize > toNumber(F_SIZE)) {
		gfn_showAlert("CW00047", "3 Mbytes" );	
		return;
	}

	//한글제목 입력금지(한글제목깨짐)
	for( var i = 0; i < length( fileDialog.FileName); i++ ) {
		if ( lengthb(charAt( fileDialog.FileName, i)) == 2 ) {
			gfn_showAlert("한글 파일명은 업로드가 불가능합니다.");
			return;
		}
	}

	ds_list.SetColumn(ds_list.currow,"FNAM" ,fileDialog.FileName);  //파일명
	ds_list.SetColumn(ds_list.currow,"FSIZE",fileSize);  //파일사이즈

}
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 29 && length(ds_list.GetColumn(nRow, "FNAM")) > 0) { // 첨부 이미지 VIEWER

		var arg = "fname_app="+quote(ds_list.GetColumn(nRow, "FNAM"))
		        + " fname="+quote(ds_list.GetColumn(nRow, "FNAM"));

		gfn_openPopup("CS1243003A", true, arg);
	}

}

var CANCELY_FLAG = false ;
var CANCELN_FLAG = false ;
var GBN_H = false;
var GBN_D = false;
/********************************************************************************
* 기      능   : 번호 해지 완료 여부 UPDATE 
********************************************************************************/
function btn_cancel_OnClick(obj)
{
	/* 초기화 */
	CANCELY_FLAG = false ;
	CANCELN_FLAG = false ;
	GBN_H = false;
	GBN_D = false;

	if(length(cb_gbn.Value) == 0) {
		alert("옵션을 선택해주세요");
		cb_gbn.SetFocus();
		return;
	}

	CANCELY_FLAG = true;

	if(cb_gbn.Value == "A") { // 전체인경우
		GBN_H = true;
		GBN_D = true;
	} else if(cb_gbn.Value == "B") {
		GBN_D = true;
	} else if(cb_gbn.Value == "C") {
		GBN_H = true;
	}

	fn_cancelUpdate();
}

/********************************************************************************
* 기      능   : 번호 해지 완료 취소 처리 UPDATE 
********************************************************************************/
function btn_cancelC_OnClick(obj)
{
	/* 초기화 */
	CANCELY_FLAG = false ;
	CANCELN_FLAG = false ;

	if(length(cb_gbn.Value) == 0) {
		alert("옵션을 선택해주세요");
		cb_gbn.SetFocus();
		return;
	}

	CANCELN_FLAG = true;

	if(cb_gbn.Value == "A") { // 전체인경우
		GBN_H = true;
		GBN_D = true;
	} else if(cb_gbn.Value == "B") {
		GBN_D = true;
	} else if(cb_gbn.Value == "C") {
		GBN_H = true;
	}

	fn_cancelUpdate();
}


function fn_cancelUpdate() {

	gfn_clearMsgBar();

    if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showAlert("CW00013");
		return;
	}

	ds_list2.ClearData();
	ds_list3.ClearData();

	var h = 0;
	var d = 0;
	var Hdat, Ddat;


	for(var i=0; i<ds_list.RowCount(); i++) {					
		if(ds_list.GetColumn(i, "CHK") == true) {

			Hdat = length(ds_list.GetColumn(i, "CS411_HDAT")) ;
			Ddat = length(ds_list.GetColumn(i, "CS411_DDAT")) ;

			if(Hdat != 0) {

				ds_list2.AddRow();
				ds_list2.CopyRow(h,ds_list,i);
				ds_list2.SetColumn(h, "FLAG", "U");
				if(CANCELY_FLAG){
					ds_list2.SetColumn(h, "CAN_FLAG", "Y");
				}

				h++;
			}
			if(Ddat != 0) {

				ds_list3.AddRow();
				ds_list3.CopyRow(d,ds_list,i);
				ds_list3.SetColumn(d, "FLAG", "U");
				if(CANCELY_FLAG){
					ds_list3.SetColumn(d, "CAN_FLAG", "Y");
				}
				d++;
			}

		}
	}

	tit_clearActionInfo();
	// ZCST411 UPDATE  HRTS 작업완료 

	if(GBN_H){
		// ZCST411 UPDATE  HRTS 작업완료 
		tit_addSaveActionInfo(
			""
			, "cs96:CS9606003B_U4"
			, ""
			, "FLAG");
	}

	if(GBN_D){
		// ZCST411 UPDATE  비통 작업완료
		tit_addSaveActionInfo(
			""
			, "cs96:CS9606003B_U5"
			, ""
			, "FLAG");
	}

	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2 ds_list3=ds_list3"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

