function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);
	// 버튼처리
	ufn_enableBtn("input", false);
	ufn_enableBtn("delete", false);
	ufn_enableBtn("save", false);

	if(G_USER_ID == "2023687" || G_USER_ID == "2021200" || G_USER_ID == "2018209" || G_USER_ID == "2018900" || G_USER_ID == "2029758" || G_USER_ID == "2999999"){
		btn_out.Visible = true;
	}
	cb_job.Index = 0;

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs96:CS9609001A_S2");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_bsu=ds_bsu"
        , ""
        , ""
        , true
        , true);
}

