/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	ds_list.row = -1;

	if(length(trim(div_code.cb_small.value)) == 0 && length(trim(div_code.cb_small.text)) > 0 
		&& trim(div_code.cb_small.text) <> "- 전체 -") {
		gfn_showAlert("사용중지된 협력사입니다.");
		return;
	}

	var user_grp = gfn_getUserGroup();

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
		}
	}

	gfn_clearGridSortMark(grd_list);

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs96:CS9609001A_S1");
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);	
}

/********************************************************************************
* 기      능   : 저장  
********************************************************************************/
function fn_save() {
	if(length(trim(div_code.cb_small.value)) == 0) {
		gfn_showAlert("협력사를 선택하여 주십시요.");
		return;
	}
	// check 된 항목들만 insert, update 처리_20141022 YAR
	var f = "";
	var j = 0;
	var k = 0;

	ds_list2.Reset();
	ds_list2.ClearData();

	ds_list3.Reset();
	ds_list3.ClearData();

	tit_clearActionInfo();

	for(var i=0; i<ds_list.rowCount(); i++) {
		f = ds_list.getColumn(i, "FLAG");

		if(ds_list.GetColumn(i, "CHK") == true) {
			if(length(ds_list.getColumn(i, "NAM")) == 0) {
				gfn_showAlert("CW00001", "성명");
                ds_list.row = i;
				return;
			}

			if(length(ds_list.getColumn(i, "BDT")) == 0) {
				gfn_showAlert("CW00001", "생년월일");
                ds_list.row = i;
				return;
			}

			if(length(ds_list.getColumn(i, "BSU")) == 0) {
				gfn_showAlert("CW00001", "업체");
                ds_list.row = i;
				return;
			}

			 if(length(ds_list.getColumn(i, "POS")) == 0) {
				 gfn_showAlert("CW00001", "직급");
                ds_list.row = i;
				 return;
			 }

			if(length(ds_list.getColumn(i, "JOB")) == 0) {
				gfn_showAlert("CW00001", "직책");
                ds_list.row = i;
				return;
			}

			if(length(ds_list.getColumn(i, "TEL")) == 0) {
				gfn_showAlert("CW00001", "전화번호");
                ds_list.row = i;
				return;
			}

			if(length(ds_list.getColumn(i, "IDT")) == 0) {
				gfn_showAlert("CW00001", "입사일자");
                ds_list.row = i;
				return;
			}
/* 2013.06.25 서비스관리부 이세희 요청
			if(length(ds_list.getColumn(i, "SID")) == 0) {
				gfn_showAlert("CW00001", "WEB ID");
                ds_list.row = i;
				return;
			}
*/
			if(ds_list.getColumn(i, "EYN") == "Y" && length(ds_list.getColumn(i, "EDT")) == 0) {
				gfn_showAlert("CW00001", "자체검사만료일");
                ds_list.row = i;
				return;
			}

			if(ds_list.getColumn(i, "EYN") == "N" && length(ds_list.getColumn(i, "EDT")) > 0) {
				gfn_showAlert("CW00001", "필수여부");
                ds_list.row = i;
				return;
			}

			if(length(ds_list.getColumn(i, "SGB")) == 0) {
				gfn_showAlert("CW00001", "성별");
                ds_list.row = i;
				return;
			}

			if(length(ds_list.getColumn(i, "ODT")) == 0 && length(ds_list.getColumn(i, "SIZ")) == 0) {
				gfn_showAlert("CW00001", "사이즈");
                ds_list.row = i;
				return;
			}

			// DATA INSERT_20141022 YAR
			if(ds_list.getColumn(i, "FLAG") =="I"){
				ds_list2.AddRow();

				ds_list2.SetColumn(k, "FLAG", ds_list.getColumn(i, "FLAG"));
				ds_list2.SetColumn(k, "ARA_NM", ds_list.GetColumn(i, "ARA_NM"));
				ds_list2.SetColumn(k, "NAM", ds_list.getColumn(i, "NAM"));
				ds_list2.SetColumn(k, "BDT", ds_list.getColumn(i, "BDT"));
				ds_list2.SetColumn(k, "SEQ", ds_list.GetColumn(i, "SEQ"));
				ds_list2.SetColumn(k, "BSU", ds_list.GetColumn(i, "BSU"));
				ds_list2.SetColumn(k, "POS", ds_list.getColumn(i, "POS"));
				ds_list2.SetColumn(k, "JOB", ds_list.getColumn(i, "JOB"));
				ds_list2.SetColumn(k, "TEL", ds_list.getColumn(i, "TEL"));
				ds_list2.SetColumn(k, "IDT", ds_list.getColumn(i, "IDT"));
				ds_list2.SetColumn(k, "ODT", ds_list.getColumn(i, "ODT"));
				ds_list2.SetColumn(k, "SID", ds_list.getColumn(i, "SID"));
				ds_list2.SetColumn(k, "EDT", ds_list.getColumn(i, "EDT"));
				ds_list2.SetColumn(k, "EYN", ds_list.getColumn(i, "EYN"));
				ds_list2.SetColumn(k, "CRT", ds_list.GetColumn(i, "CRT"));
				ds_list2.SetColumn(k, "TEM_NM", ds_list.GetColumn(i, "TEM_NM"));
				ds_list2.SetColumn(k, "SGB", ds_list.GetColumn(i, "SGB"));
				ds_list2.SetColumn(k, "EYN", ds_list.GetColumn(i, "EYN"));
				ds_list2.SetColumn(k, "EML", ds_list.GetColumn(i, "EML"));
				ds_list2.SetColumn(k, "SIZ", ds_list.GetColumn(i, "SIZ"));
				ds_list2.SetColumn(k, "CDT", ds_list.GetColumn(i, "CDT"));

				k++;
			}
			// DATA UPDATE 
			if(ds_list.getColumn(i, "FLAG") == "U"){
				ds_list3.AddRow();

				ds_list3.SetColumn(j, "FLAG", ds_list.getColumn(i, "FLAG"));
				ds_list3.SetColumn(j, "ARA_NM", ds_list.GetColumn(i, "ARA_NM"));
				ds_list3.SetColumn(j, "NAM", ds_list.getColumn(i, "NAM"));
				ds_list3.SetColumn(j, "BDT", ds_list.getColumn(i, "BDT"));
				ds_list3.SetColumn(j, "SEQ", ds_list.GetColumn(i, "SEQ"));
				ds_list3.SetColumn(j, "BSU", ds_list.GetColumn(i, "BSU"));
				ds_list3.SetColumn(j, "POS", ds_list.getColumn(i, "POS"));
				ds_list3.SetColumn(j, "JOB", ds_list.getColumn(i, "JOB"));
				ds_list3.SetColumn(j, "TEL", ds_list.getColumn(i, "TEL"));
				ds_list3.SetColumn(j, "IDT", ds_list.getColumn(i, "IDT"));
				ds_list3.SetColumn(j, "ODT", ds_list.getColumn(i, "ODT"));
				ds_list3.SetColumn(j, "SID", ds_list.getColumn(i, "SID"));
				ds_list3.SetColumn(j, "EDT", ds_list.getColumn(i, "EDT"));
				ds_list3.SetColumn(j, "EYN", ds_list.getColumn(i, "EYN"));
				ds_list3.SetColumn(j, "CRT", ds_list.GetColumn(i, "CRT"));
				ds_list3.SetColumn(j, "TEM_NM", ds_list.GetColumn(i, "TEM_NM"));
				ds_list3.SetColumn(j, "SGB", ds_list.GetColumn(i, "SGB"));
				ds_list3.SetColumn(j, "EYN", ds_list.GetColumn(i, "EYN"));
				ds_list3.SetColumn(j, "EML", ds_list.GetColumn(i, "EML"));
				ds_list3.SetColumn(j, "SIZ", ds_list.GetColumn(i, "SIZ"));
				ds_list3.SetColumn(j, "CDT", ds_list.GetColumn(i, "CDT"));

				// UPDATE문의 조건절을 위한 컬럼 (변경 전 데이터)
				ds_list3.SetColumn(j, "NAM2", ds_list.GetOrgColumn(i, "NAM"));
				ds_list3.SetColumn(j, "BDT2", ds_list.GetOrgColumn(i, "BDT"));
				ds_list3.SetColumn(j, "SEQ2", ds_list.GetOrgColumn(i, "SEQ"));

				j++;
			}	

		}
	}	
		if(k > 0)
		{
			tit_addMultiActionInfo("cs96:CS9609001A_I1");
		}
		if(j > 0)
		{
			tit_addMultiActionInfo("cs96:CS9609001A_U1");
		}

		 tit_callService(
		""
		, ""
		,"ds_list2=ds_list2 ds_list3=ds_list3 ds_cond=ds_cond"
		, ""
		, ""
		, "fn_afterSave"
		, true);


//	gfn_clearGridSortMark(grd_list);

//	tit_clearActionInfo();

//	if(ds_list.rowcount > 0) {
//		tit_addSingleActionInfo("cs96:CS9609001A_D1");
//		tit_addMultiActionInfo("cs96:CS9609001A_D1");
//		tit_addMultiActionInfo("cs96:CS9609001A_I1");
//	} else {
//		return;
//		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
//		tit_addSingleActionInfo("cs96:CS9609001A_D2");
//	}

//	tit_callService(
//		""
//		, ""
//		, "ds_list=ds_list:A ds_cond=ds_cond"
//		, ""
//		, ""
//		, "fn_afterSave"
//		, true);
}

/********************************************************************************
* 기      능   : 입력
********************************************************************************/
function fn_input() {
	if(length(trim(div_code.cb_small.value)) == 0) {
		gfn_showAlert("협력사를 선택하여 주십시요.");
		return;
	}

	var nRow = gfn_addGridRow(grd_list, -1, "FLAG");
	ds_bsu.Filter("CODE == " + quote(trim(div_code.cb_small.value)));
	ds_list.SetColumn(nRow, "BSU", trim(div_code.cb_small.value));
	ds_list.SetColumn(nRow, "JOB", cb_job.Value);
	cb_job.Index = 0;
	cb_eyn.Index = 0;
	cb_sgb.Index = 0;
	cb_siz.Index = 0;

	ed_inam.SetFocus();
}

/********************************************************************************
* 기      능   : 삭제 
********************************************************************************/
function fn_delete() {
	// if(ds_list.rowcount > 0 && length(ds_list.GetColumn(ds_list.row, "CDT")) == 0) {
		// if(length(trim(div_code.cb_small.value)) == 0) {
			// gfn_showAlert("협력사를 선택하여 주십시요.");
			// return;
		// }
		// ds_list.DeleteRow(ds_list.row);
	// }

	//check 된 데이터 삭제_20141022 YAR
	ds_list4.Reset();
	ds_list4.ClearData();

	var k = 0;
	var j = 0;

	for(var i=0; i<ds_list.rowCount(); i++) {	
		if(ds_list.GetColumn(i, "CHK") == true) {
			ds_list4.AddRow();

			ds_list4.SetColumn(k, "NAM", ds_list.getColumn(i, "NAM"));
			ds_list4.SetColumn(k, "BDT", ds_list.getColumn(i, "BDT"));
			ds_list4.SetColumn(k, "SEQ", ds_list.GetColumn(i, "SEQ"));
			ds_list4.SetColumn(k, "BSU", ds_list.GetColumn(i, "BSU"));
			ds_list4.SetColumn(k, "POS", ds_list.getColumn(i, "POS"));
			ds_list4.SetColumn(k, "ARA_NM", ds_list.GetColumn(i, "ARA_NM"));

			k++;
			// 입력버튼 누르고 추가한 ROW 삭제
			if(length(ds_list.GetColumn(ds_list.row, "CDT")) == 0)
			{
				ds_list.DeleteRow(ds_list.row);
			}
		}
	}
		tit_clearActionInfo();
		tit_addMultiActionInfo("cs96:CS9609001A_D3");

		tit_callService(
		""
		, ""
		, "ds_list4=ds_list4"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

