function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	// if(nCell == 0) {
		// gfn_selectGridCol(obj); 
	// }
	// if(nCell == 1){
		// gfn_selectAllGrid2(obj,0,0,true);
	// } 
	// else(nCell > 1) {
		// gfn_sortGrid(obj,nCell);
	// }
	if(nCell == 1) {
		// 그리드 표시할 컬럼 선택
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리
		gfn_sortGrid(obj,nCell);
	}
}

/********************************************************************************
* 기      능   :  변경시 마다 
********************************************************************************/
function ds_list_OnRowPosChanged(obj,nOldRow,nRow) {
	if(nRow == -1) {
		return;
	}

	if(ds_list.getColumn(nRow, "FLAG") == "I") {
		ed_inam.SetFocus();
		ufn_enableBtn("save", true);
	} else {
		ds_list.fireevent = false;
		ufn_enableBtn("save", true);
		ds_list.fireevent = true;
	}
}

/********************************************************************************
* 기      능   :  데이터 변경시 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	gfn_changeFlag(obj, strColumnID, nRow, "FLAG");
}

function ds_job_grd_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	ds_list.SetColumn(nRow, "JOB", cb_job.Value);
}

function ds_sgb_grd_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	ds_list.SetColumn(nRow, "SGB", cb_sgb.Value);
}

function ds_siz_grd_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	ds_list.SetColumn(nRow, "SIZ", cb_siz.Value);
}

function btn_out_OnClick(obj)
{
	ds_list5.Reset();
	ds_list5.ClearData();

	var k = 0;
	for(var i=0; i<ds_list.rowCount(); i++) {	
		if(ds_list.GetColumn(i, "CHK") == true) {
			ds_list5.AddRow();
			ds_list5.SetColumn(k, "USERNUMB", ds_list.getColumn(i, "USERNUMB"));	
			k++;
		}
	}
	tit_clearActionInfo();
	tit_addMultiActionInfo("cs96:CS9609001A_U2");

	tit_callService(
	""
	, ""
	, "ds_list5=ds_list5"
	, ""
	, ""
	, "fn_afterSave"
	, true);

}
