function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	var codeArr = ["CS115"];
	var dsObjArr = [ds_ara];
	var addTypeArr = [G_CODE_ALL,G_CODE_SELECT];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	// 2019.06.19 공통 권한함수 추가. Han J.H
	// 저장버튼과 입력버튼을 권한존재시 활성화하거나 비활성화 시킨다.
	//===========================================================================================================
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS9610002A");
	ds_rcond.SetColumn(0, "GBN", "01");	
	write_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();

	if(write_auth == true) {
		ufn_enableBtn("save", true);
		ufn_enableBtn("input", true);		
	} else {
		ufn_enableBtn("save", false);
		ufn_enableBtn("input", false);	
	}

	// MOD PM의 경우 전체지역으로 조회되도록 권한을 부여한다.
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS9610002A");
	ds_rcond.SetColumn(0, "GBN", "02");	
	pm_sel_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();

	// 업체 및 인원 삭제버튼 활성화 권한
	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS9610002A");
	ds_rcond.SetColumn(0, "GBN", "03");	
	//pm_sel_auth = gfn_getAuth(ds_rcond);
	com_del_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();		
	//===========================================================================================================

	var user_grp = gfn_getUserGroup();

	if(user_grp < "40" && pm_sel_auth != true){ // 관리자 권한이 아니면서 MOD PM 권한이 아니면 전체지역으로 조회하는것을 비활성화.
		cb_ara.Value = G_AREA_CODE;
		cb_ara.Enable = false;
	}

	btn_user.Enable = false;
	ufn_setBsu();
}

