/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	ds_list.row = -1;
	ds_list.ClearData();
	ds_user.ClearData();
	ds_cond.SetColumn(0, "ARA", cb_ara.Value);

	gfn_clearGridSortMark(grd_list);	
	if(fn_QueryCheck()){

		tit_clearActionInfo();
		tit_addSearchActionInfo("cs96:CS9610002A_S1");
		tit_addSearchActionInfo("cs96:CS9610002A_S3");
		tit_callService(
			""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list ds_user=ds_user"
			, ""
			, "fn_afterQuery"
			, true);	
	}   
}

/********************************************************************************
* 기      능   : 저장  
********************************************************************************/
function fn_save() {

	if(ds_list.rowCount() < 1) {
		// 저장할 정보 없음.
		gfn_showMsg("CW00010");
		return;
	}

	var f = "";
	for(var i=0; i<ds_list.rowCount(); i++) {
		f = ds_list.getColumn(i, "FLAG");
		if(f == "I" || f == "U") {	
			if(length(ds_list.getColumn(i, "NAM")) == 0) {
				gfn_showAlert("CW00001", st_bsu_nm.text);
				ds_list.row = i;
				return;
			}	
			if(length(ds_list.getColumn(i, "LIFNR")) == 0) {
				gfn_showAlert("CW00001", st_lifnr.text);
				ds_list.row = i;
				return;
			}	
			if(length(ds_list.getColumn(i, "ARA")) == 0) {
				gfn_showAlert("CW00001", st_ara2.Value);
				ds_list.row = i;
				return;
			}
			if(length(ds_list.getColumn(i, "NUM")) == 0) {
				gfn_showAlert("CW00001", st_num.Value);
				ds_list.row = i;
				return;
			}
			if(length(ds_list.getColumn(i, "AD1")) == 0) {
				gfn_showAlert("CW00001", st_addr.Value);
				ds_list.row = i;
				return;
			}
			if(ds_list.getColumn(i, "CPT") == 0) {
				gfn_showAlert("CW00001", st_cpt.Value);
				ds_list.row = i;
				return;
			}
			if(length(ds_list.getColumn(i, "REP")) == 0) {
				gfn_showAlert("CW00001", st_cpt.Value);
				ds_list.row = i;
				return;
			}
			if(length(ds_list.getColumn(i, "FNA")) == 0) {
				gfn_showAlert("CW00001", st_fna.Value);
				ds_list.row = i;
				return;
			}
		}
	}
	if(length(med_cpt.Value) == 0) ds_list.SetColumn(0, "CPT", 0);
	if(length(med_per.Value) == 0) ds_list.SetColumn(0, "PER", 0);
	if(length( ed_fileName.value ) > 0 && length(ds_list.getColumn(0, "FILE_STATE"))>0 ) {
	// 첨부 파일 처리 

		var arr = tit_fileWrite(httpFile, "AP", ds_list.getColumn(0, "FILE_PATH"), ds_list.getColumn(0, "FNA"), "", "", "" );  
		// 처리 결과 확인하기 

		if( arr[0] == "S" ) {
		// 성공 
			ds_list.setColumn(0, "FNA", arr[2]);	// 파일 명 
			ds_list.setColumn(0, "FSZ", fileSize);
		} else {
			gfn_showAlert( arr[1] );
			return;
		}
	}
	gfn_clearGridSortMark(grd_list);
	tit_clearActionInfo();
	tit_addSaveActionInfo(
        "cs96:CS9610002A_I1"
        , "cs96:CS9610002A_I1"
        , ""
        , "FLAG");

    tit_addSaveActionInfo(
			""
			, "cs96:CS9610002B_I1"
			, ""
			, "FLAG"
			);


	tit_callService(
		""
		, ""
		, "ds_list=ds_list ds_user=ds_user"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

