// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showSearchMsg(grd_list, errCode, errMsg);	

	if(length(cb_bsu.Value) > 0) {
		btn_user.Enable = true;

		// 2019.12.23 보수업체가 조회된 상태여야만 업체 및 인원삭제 버튼 활성화 되도록 처리
		if(com_del_auth == true) {
			btn_com_del.Visible = true;
			btn_com_del.Enable = true;
		}
	}

	if(ds_list.rowcount > 0)
	{
		ed_fileName.value = ds_list.getColumn(0, "FNA");
		disableFile();		
	} 
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		ds_cond.SetColumn(0, "ARA", ed_newara.Value);
		fn_query();
	}
}

