********************************************************************************/
function fn_queryCheck(){

	if(length(ds_cond.GetColumn(0,"ARA"))==0){
		gfn_showAlert("CW00001","지역");
		return false;
	}	
	return true;

	if(length(ds_cond.GetColumn(0,"BSU"))==0){
		gfn_showAlert("CW00001","업체명");
		return false;
	}	
	return true;
}

