var strZipcodeObj;
function ufn_openZipcode(Obj) {
    var arg = "strObj="+quote(Obj.id);
    strZipcodeObj=Obj.id;
    AxMSIE0.Navigate2(G_SERVER_URL + "addressSearchLink.jsp");
}

var chk = 0;
function AxMSIE0_TitleChange(obj,Text) {
    chk++;
    if(Text != "address link") {
        if(chk == 2) {
            ufn_setAddr();
            chk = 1;
        }
    } else {
        chk = 1;
    }
}

function ufn_setAddr() {
    var objDocument = AxMSIE0.Document;

    var roadAddrPart1 = objDocument.getElementById('roadAddrPart1').value;
    var roadAddrPart2 = objDocument.getElementById('roadAddrPart2').value;

    var roadAddr = roadAddrPart1 + ' ' + roadAddrPart2;
    var addrDetail = objDocument.getElementById('addrDetail').value;
    var zipNo = objDocument.getElementById('zipNo').value;

    var tmpAddr = roadAddr + ' ' + addrDetail;

    var tmpRow = ds_list.currow;

	ed_zipcode.value = zipNo;
	ed_addr.value = roadAddr;
	ed_addr2.value = addrDetail;

}

/********************************************************************************
* 기      능   : 업체 및 인원정보 삭제 기능
********************************************************************************/
function btn_comDelete() {

	if(ds_list.GetColumn(0,"LIFNR") == '' || ds_list.GetColumn(0,"LIFNR").Length() < 10 ) {
		gfn_showAlert("CW00001", "사업자번호");
		return;
	}

	if(gfn_showConfirm("업체 및 인원정보를 삭제하시겠습니까? 삭제된 정보는 복구되지 않으니 확인해주시기 바랍니다."))
	{
		ds_comdel.AddRow();

		ds_comdel.SetColumn(0, "LIFNR", ds_list.GetColumn(0,"LIFNR"));

		tit_clearActionInfo();

		tit_addMultiActionInfo("cs96:CS9610002A_D1"); 
		tit_addMultiActionInfo("cs96:CS9610002A_D2");

		tit_callService(
			""
			, ""
			, "ds_comdel=ds_comdel"
			, ""
			, ""
			, "fn_afterComDelete"
			, true
			, false
			, true);		
	}		
}

function fn_afterComDelete(errCode, errMsg) {
	ds_comdel.ClearData();

	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showAlert("CI00004");
		// 지역에 해당하는 설치면허업체 리스트 갱신
		ufn_setBsu();
		// 갱신된 설치면허업체 리스트중 제일 첫번째 업체를 기준으로 재조회
		ds_cond.SetColumn(0, "BSU", ds_bsu.GetColumn(1, "CODE"));
		fn_query();
	}	
}
