function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);
	div_code.btn_delete.Enable = false;
	chk_tempLifnr.Value = true;

	gfn_initDs(ds_rcond, true);
	ds_rcond.SetColumn(0, "ZPROGRAM", "CS9612001A");
	ds_rcond.SetColumn(0, "GBN", "01");	
	print_auth = gfn_getAuth(ds_rcond);
	ds_rcond.ClearData();

	if(print_auth == true) { // 이력카드 출력, 특이사항
		btn_print_profile.Visible = true;
		btn_add_infomation.Visible = true;
	}
	/*if(G_USER_ID == "2021200" || G_USER_ID == "2017938" || G_USER_ID == "2021153" || G_USER_ID == "2018900" || G_USER_ID == "2029758") {
		btn_print_profile.Visible = true;
		btn_add_infomation.Visible = true;
	}*/
}

