/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
	ds_list.row = -1;

	var user_grp = gfn_getUserGroup();

	if(user_grp == '40') {
		if(gfn_isNull(div_code.cb_big, true, st_bsu.value))
		return;
	}

	if(user_grp == "10") { // 보수협력사일 경우
		ds_cond.SetColumn(0, "BSU", G_LGORT);
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
		ds_cond.SetColumn(0, "VKBUR", '');

		ds_cond_0.SetColumn(0, "LGORT", G_LGORT);
	} else if(user_grp == "20") { // 보수PM일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", '');
		ds_cond.SetColumn(0, "BPM", G_USER_ID);
		ds_cond.SetColumn(0, "ARA", '');
		ds_cond.SetColumn(0, "VKBUR", '');

		ds_cond_0.SetColumn(0, "LGORT", trim(div_code.cb_small.value));
	} else if(user_grp == "30") { // 지방사무소일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond.SetColumn(0, "TEM", G_SAP_USER_VGCD);
		ds_cond.SetColumn(0, "BPM", '');
		ds_cond.SetColumn(0, "ARA", '');
		ds_cond.SetColumn(0, "VKBUR", '');

		ds_cond_0.SetColumn(0, "LGORT", trim(div_code.cb_small.value));
	} else if(user_grp == "40") { // 고객지원부일 경우
		ds_cond.SetColumn(0, "BSU", trim(div_code.cb_small.value));
		ds_cond_0.SetColumn(0, "LGORT", trim(div_code.cb_small.value));
		if(div_code.cb_big.value == '1') { // 협력사 콤보1이 지역일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "VKBUR", '');
		} else if(div_code.cb_big.value == '2') { // 협력사 콤보1이 PM일 경우
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "VKBUR", '');
		} else if(div_code.cb_big.value == '4') { // 부서 조회 
			ds_cond.SetColumn(0, "TEM", '');
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "VKBUR", trim(div_code.cb_mid.value));
		} else { // 협력사 콤보1이 팀일 경우
			ds_cond.SetColumn(0, "TEM", trim(div_code.cb_mid.value));
			ds_cond.SetColumn(0, "BPM", '');
			ds_cond.SetColumn(0, "ARA", '');
			ds_cond.SetColumn(0, "VKBUR", '');
		}
	}

	gfn_clearGridSortMark(grd_list);

	tit_clearActionInfo();

	tit_addSearchActionInfo("cs96:CS9612001A_S4");

	// 직영/협력사 정보 구하기. 2019.01.11 Han J.H
	tit_callService(
          ""
        , ""
        , "ds_cond_0=ds_cond_0"
        , "ds_bsuInfo=ds_bsuInfo"
        , ""
        , "fn_afterBsu"
        , true
        , false
        , true);	

	/*tit_addSearchActionInfo("cs96:CS9612001A_S1");
	tit_callService(
          ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);	*/


}

/********************************************************************************
* 기      능   : 저장  
********************************************************************************/
function fn_save() {

	if(ds_list.rowCount() < 1) {
		// 저장할 정보 없음.
		gfn_showMsg("CW00010");
		return;
	}

	var f = "";
	for(var i=0; i<ds_list.rowCount(); i++) {
		f = ds_list.getColumn(i, "FLAG");
		if(f == "I" || f == "U") {	

			if(length(ds_list.getColumn(i, "LIFNR")) == 0) {
				gfn_showAlert("CW00001", st_lifnr.text);
				ds_list.row = i;
				return;
			}	
			//자본금 체크	
			if(ds_list.getColumn(i, "CPT") == "") {
				gfn_showAlert("CW00001", st_cpt.text);
				ds_list.row = i;
				return;
			}
			//유지관리범위 체크	
			if(ds_list.getColumn(i, "SCP") == "") {
				gfn_showAlert("CW00001", st_scp.text);
				ds_list.row = i;
				return;
			}

		}
	}

	gfn_clearGridSortMark(grd_list);

	tit_clearActionInfo();
	tit_addSaveActionInfo(
        "cs96:CS9612001A_I1"
        , "cs96:CS9612001A_I1"
        , ""
        , "FLAG");

	tit_callService(
		""
		, ""
		, "ds_list=ds_list"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

/********************************************************************************
* 기      능   : 입력
********************************************************************************/
function fn_input() {

	gfn_clearMsgBar();
	ds_list.ClearData();
	gfn_addGridRow(grd_list, -1, "FLAG");
	ds_list.SetColumn(ds_list.currow, "CHK", "1");
	med_cpt.Value = 0;		
	med_scp.Value = 0;		

}

