********************************************************************************/
function fn_excel() {

	if(ds_list.rowCount() < 1) {
		// 출력할 정보 없음.
		gfn_showMsg("CW00010");
		return;
	}
	gfn_saveToExcel(grd_list, true);
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 직영/협력사 구하기 CallBack  
********************************************************************************/
function fn_afterBsu(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("업체정보가 존재하지 않습니다. 서비스 관리부에 문의바랍니다.");
	} else {
		if(ds_bsuInfo.GetColumn(0, "BSU_GB") != '') {
			ds_cond.SetColumn(0, "BSU_GB", ds_bsuInfo.GetColumn(0, "BSU_GB"));
		}

		tit_clearActionInfo();

		tit_addSearchActionInfo("cs96:CS9612001A_S1");
		tit_callService(
			  ""
			, ""
			, "ds_cond=ds_cond"
			, "ds_list=ds_list"
			, ""
			, "fn_afterQuery"
			, true
			, false
			, true);
	}
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	gfn_showSearchMsg(grd_list, errCode, errMsg);		
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

