function ufn_openWindow(obj)
{
	var arg = "comName="+quote(ed_bsu_nm.Text);
	var rtn = gfn_openPopup("WB0101001C",true,arg);
	if ( rtn != null ) 
	{
		ed_bsu_nm.value   = rtn[1];
		ed_lifnr.value    = rtn[0];
	//	ed_rname.value    = rtn[2];
	//	ed_tel.value      = rtn[3];
		ed_zipcode.value  = rtn[4];
		ed_addr.value     = rtn[5];
	}
}

function cb_ls4_OnClick(obj,strValue)
{
	if(cb_ls4.Value == 1) {
		ed_lcr.Enable = true;
	} else{
		ed_lcr.Enable = false;
		ed_lcr.Text = "";
	}
}

function cb_is5_OnClick(obj,strValue)
{
	if(cb_is5.Value == 1) {
		ed_isr.Enable = true;
	} else{
		ed_isr.Enable = false;
		ed_isr.Text = "";
	}

}
/********************************************************************************
* 기      능   :  이력카드 출력 
********************************************************************************/
function btn_print_profile_OnClick(obj)
{
	if(ds_list.FindRow("CHK", 1) == -1) {
		gfn_showAlert("CW00013");		//처리할 자료를 먼저 선택해 주십시오.
		return;
	}


	var q_str="";
	var num = 0;

	for(i=0 ; i < ds_list.RowCount() ; i++) {
		if(ds_list.GetColumn(i, "CHK") == true) {
			num = num + 1 ;
			var t_lifnr = ds_list.GetColumn(i, "LIFNR");

			if ( num == 1 ) {
				q_str = "'" + t_lifnr + "'" ;
			} else { 
				q_str = q_str + ",'"+t_lifnr+"' ";
			}
		}
	} 

	ds_cond.SetColumn(0, "LIFNR", q_str);

	ds_personnel.ClearData();
	ds_profile.ClearData();

	tit_clearActionInfo();
	tit_addSearchActionInfo("cs96:CS9612001A_S2"); // 인원 현황 ds_personnel
	tit_addSearchActionInfo("cs96:CS9612001A_S3"); // ds_profile

	tit_callService(
		  ""
		, ""
		, "ds_cond=ds_cond"
		, "ds_personnel=ds_personnel ds_profile=ds_profile"
		, ""				
		, ""					
		, true							
		, false							
		, true);	


	Dialog("CS96::CS9612001C.xml", "");
}

/********************************************************************************
* 기      능   :  업체별 추가 정보 관리 
********************************************************************************/

function btn_add_infomation_OnClick(obj)
{
	if(ds_list.rowCount() < 1) {
		// 저장할 정보 없음.
		gfn_showMsg("업체를 선택하십시오.");
		return;
	}

	var arg = "lifnr=" + quote(ds_list.GetColumn(ds_list.row, "LIFNR"))
	        + " gubun=" + quote("Remark") ;
	gfn_openPopup("CS9612001D", true, arg);
}

function btn_import_evaluation_OnClick(obj)
{
	var arg = "lifnr=" + quote("")
	        + " gubun=" + quote("Evaluation") ;
	gfn_openPopup("CS9612001D", true, arg);
}
