function fn_OnLoadCompleted(obj) {

	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm();
	gfn_initDs(ds_cond, true);

	var userGroup = gfn_getUserGroup();                                 //사용자 권한그룹
//	if(userGroup != "40")                                          //그룹이 40인 경우를 제외하고는 해당 항목을 감춘다.
//	{
		   grd_List1.SetCellProp("Head", 7, "Display", "null");           //7 :자재원가
		   grd_List1.SetCellProp("Body", 7, "Display", "null");
		   grd_List1.ChangeColWidth("", 7, 0);
//	}	

	//자재원가 감추기
	grd_List1.SetCellProp("Head", 6, "Display", "null");                  //6:계산원가
	grd_List1.SetCellProp("Body", 6, "Display", "null");
	grd_List1.ChangeColWidth("", 6, 0);

	grd_List1.SetCellProp("Head", 8, "Display", "null");                  //8:비율
	grd_List1.SetCellProp("Body", 8, "Display", "null");
	grd_List1.ChangeColWidth("", 8, 0);
	grd_List1.SetCellProp("Head", 9, "Display", "null");                  //9:가중치
	grd_List1.SetCellProp("Body", 9, "Display", "null");
	grd_List1.ChangeColWidth("", 9, 0);

	grd_List2.SetCellProp("Head", 7, "Display", "null");                  //7:자재원가
	grd_List2.SetCellProp("Body", 7, "Display", "null");
	grd_List2.ChangeColWidth("", 7, 0);



	/**************************/
	/* 호시 속도정보 설정 필요*/
	/**************************/

	// 호기 속도정보 : parent에서 팝업 호출시 argument 로 fa_hty 전송
	//alert('fa_hty:' + fa_hty);

	if (isExistVar("fa_hty", true) ) {
		//일반중속(true)과 고속(false) 분리 처리

	//일반중속(true)과 고속(false) 분리 처리
		var speed = true;

		if (fa_hty == 'Y')
			speed = false;
		else 
			speed = true;

		if(speed) {
	//		alert("일반중속");
			Tab00.tab1.Enable = true;
			Tab00.tab2.Enable = false;
			Tab00.tab3.Enable = true;
			Tab00.TabIndex = 0; 
		} else {
	//		alert("고속");
			Tab00.tab1.Enable = false;
			Tab00.tab2.Enable = true;
			Tab00.tab3.Enable = true;
			Tab00.TabIndex = 1; 
		}
	} else {
		v_speed.Text = "";
		Tab00.TabIndex = 0; 
	}	
	//호기정보 설정
	//호기번호 : parent에서 팝업 호출시 argument로 fa_hogin 전송

	//alert('fa_hogin:' + fa_hogin);

	v_hogin.Text = fa_hogin;
	//v_hogin.Text = '112330L01';

	fn_queryblock();

}
