function fn_query() {

	ds_cond.ClearData();
	ds_cond.AddRow();


	if (Combo5.Index == 0) {
		ds_cond.SetColumn(0,"MATNR", in_value.Text);
	} else if (Combo5.Index == 1) {
		ds_cond.SetColumn(0,"MATNR_NM", in_value.Text);
	} 

	ds_block_dp.ClearData();
	ds_list1.ClearData();
	ds_list2.ClearData();

	tit_clearActionInfo();

	tit_addSearchActionInfo("cs96:CS9614001C_S5", null, true);
	tit_callService(
		  ""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list1=ds_list1"
		, ""
		, "fn_afterQuery"
		, true);
}

