/********************************************************************************
* 기      능   : clear 및 블럭 설정 값
********************************************************************************/
function uf_setBlock(idx)
{

	ds_block_dp.ClearData();
	ds_list1.ClearData();
	ds_list2.ClearData();

//alert("set block");
//alert(idx);

	//trace(ds_block.SaveXml());

	for (i=1; i < 15 ; i++) {

		var blk = ds_block.GetColumn(idx, "CS612_BLK"+i);
		var blk_nm  = ds_block.GetColumn(idx, "BLK_NM"+i);
		if(length(blk)>0) {
			ds_block_dp.AddRow();
			ds_block_dp.SetColumn(i-1, "BLK", blk);
			ds_block_dp.SetColumn(i-1, "BLK_NM", blk_nm);;
		} else {
			break;
		}
	}

	ds_block_dp.row=0;
	//trace(ds_block_dp.SaveXml());

}


