	//var s_blk = "'%"+ds_block_dp.GetColumn(ds_block_dp.currow, "BLK")+"%'";
	var s_blk = "'%"+ds_block_dp.GetColumn(grd_block.ClickedRow, "BLK")+"%'";

	//alert(s_blk);
	ds_list1.ClearData();
	ds_list2.ClearData();


	tit_clearActionInfo();
	tit_addSearchActionInfo("cs96:CS9614001C_S1", null, true);

	tit_callService(
			  ""
			, ""
			, ""
			, "ds_list1=ds_list1"
			, "BLOCK=" + quote( s_blk )
			, "fn_afterQuery1"						//이종석-fn_afterQuery를 fn_afterQuery1으로 바꿈
			, false
			, true);

}



