/********************************************************************************
* 기      능   : 확인 80
********************************************************************************/
function fn_confirm() {

	//ds_list2 에 값이 없을 경우 선택 메시지 전송후 return
	if (ds_list2.rowcount == 0 ) {
		alert("2 Level에 선택한 자재 값이 없습니다.");
		return;
	}


	ds_return.ClearData();

	// 선택 된 값 복사
	gfn_copyDs(ds_list2, ds_return);

	//alert("select count " + ds_return.rowcount);

	var crow = ds_return.rowcount;


	// 1 Level 값 추가
	if (ds_list1.rowcount > 0 ) {
		var lrow = ds_list1.currow;

		ds_return.AddRow();
		ds_return.CopyRow(crow,ds_list1,lrow);
		ds_return.SetColumn(crow,"SAMT1", ds_list1.GetColumn(lrow,"SAMTDP"));
	}

	// DataSet 내용 확인
	//trace(ds_return.SaveXML());

	// DB insert 처리
	//Action을 Clear한다.
	tit_clearActionInfo();

	tit_addSearchActionInfo("cs96:CS9614001C_T1",true);
	tit_addSearchActionInfo("cs96:CS9614001C_S4",true);

	//ZMMT031에 데이터를 INSERT 한다.
	tit_addMultiActionInfo("cs96:CS9614001C_I1");	

	tit_callService(
        ""
        , ""
        , "ds_return=ds_return"
        , "ds_key = ds_key"
        , ""
        , "fn_afterSaveRequest");	

}


// Traction Machine block 호출
function bt_OnClick(obj)
{
	//
	//button ID 값이 key 값이 되도록 명명하여야 함.
	//
	var idx = 0;

	idx = obj.ID.ToString().substr(3,2).toInteger();

	//alert("인덱스 값 :  "+idx);
	//alert("Tab ID : " + Tab00.GetCurrentComponent().ID);

	if(Tab00.GetCurrentComponent().ID == 'tab1') {
		idx = idx - 1 ;  // Luxen 시작 값은 0 부터 시작.
	} else if(Tab00.GetCurrentComponent().ID == 'tab2') {
		idx = idx + 33 ;  // MRL 시작 값은 +33 부서 시작
	} else if (Tab00.GetCurrentComponent().ID == 'tab3') {
		idx = idx + 66;  // 66 은 카 
	}

	uf_setBlock(idx);

//	if(G_USER_ID == "2017750" || G_USER_ID == "2021200") {
		if(idx == 67) { // CEILING
			gfn_openPopup("CS9614001E", true, "knd=A");
		} else if(idx == 76) { // MAIN OPB
			gfn_openPopup("CS9614001E", true, "knd=B");
		} else if(idx == 78) { // DISABLE OPB
			gfn_openPopup("CS9614001E", true, "knd=C");
		} else if(idx == 79) { // HANDRAIL
			gfn_openPopup("CS9614001E", true, "knd=D");
		} else if(idx == 71) { // CAR WALL
			gfn_openPopup("CS9614001E", true, "knd=E");
		} else if(idx == 21) { // CAR DOOR
			gfn_openPopup("CS9614001E", true, "knd=F");
		} else if(idx == 31) { // HATCH DOOR
			gfn_openPopup("CS9614001E", true, "knd=G");
		} else if(idx == 32) { // JAMB
			gfn_openPopup("CS9614001E", true, "knd=H");
		}
//	}
}

/********************************************************************************
* 기      능   : 그리드 셀 클릭
********************************************************************************/
function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 0) {
		var samtdp = ds_list1.GetColumn(ds_list1.currow,"SAMT1");
		var samt_l2 = ds_list2.GetColumn(nRow,"SAMT2");
		var price = 0;
		var ratio = ds_list1.GetColumn(ds_list1.currow,"RATIO");
//		alert(ratio);
		var weight = ds_list1.GetColumn(ds_list1.currow,"WEIGHT");
//		alert(weight);
//		alert("SAMTDP :"+samtdp);
//		alert("SAMT L2 :" +samt_l2);
		if (ds_list2.GetColumn(nRow,nCell)) {
			price = samtdp-samt_l2;
			ds_list1.SetColumn(ds_list1.currow,"SAMT1", price);
		} else { 
			price = samtdp+samt_l2;
			ds_list1.SetColumn(ds_list1.currow,"SAMT1", price);
		}

//		alert(price);

		price = ToNumber(price) * ToNumber(ratio) * ToNumber(weight);

//		alert(price);

		ds_list1.SetColumn(ds_list1.currow,"SAMTDP", price); 
	}
}

/********************************************************************************
* 기      능   : Tab 변경시 일반중속, 고속 filtering 해서 못들어 가도록 구성
********************************************************************************/
function tab00_OnChanged(obj,nOldIndex,nNewindex)
{
	if (Tab00.tab1.Enable) {
		//일반 중속일 경우
		if (nNewindex == 1) {
			Tab00.TabIndex = 0;
		}
	} else {
		//고속일 경우
		if (nNewindex == 0 ) {
			Tab00.TabIndex = 1;
		}
	}
}


