//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리
	gfn_showSearchMsg(grd_list);

	if (ds_list.RowCount() > 0)
	{
		for(var i = 0; i < ds_list.rowcount ; i++)
		{
			if (length(ds_list.GetColumn(i, "CS655_NAM")) > 0)
			{
			   ds_list.SetColumn(i,"L_CS655_NAM", tit_getLocalFileName(ds_list.getColumn(i, "CS655_NAM")));
			}
		}
	}
}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if(errCode != 0) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}

